import { StatusBar } from 'expo-status-bar';
import React, { useContext } from 'react';
import { StyleSheet, View, Pressable } from 'react-native';
import { Button, ButtonGroup, withTheme, Text, Input, Icon } from 'react-native-elements';
import { createBottomTabNavigator } from '@react-navigation/bottom-tabs';
import { auth } from "../firebase";
import { RootStackParamList, RootTabParamList, RootTabScreenProps } from '../types';
import HomeScreen from './HomeScreen';
import TabTwoScreen from './TabTwoScreen';
import { Feather } from '@expo/vector-icons';
import Colors from '../constants/Colors';
import { getHeaderTitle } from '@react-navigation/elements';
import { LinearGradient } from 'expo-linear-gradient';

export default function RootScreen({ navigation }: { navigation: any }) {

  const BottomTab = createBottomTabNavigator<RootTabParamList>();
  return (
    <BottomTab.Navigator
      initialRouteName="Home"
      screenOptions={{
        tabBarActiveTintColor: '#000396',
        tabBarStyle: { borderTopWidth: 0, height: '8%' },
        tabBarBackground: () => (
          <View style={{ flex: 1, backgroundColor: 'rgba(255,255,255,0.1)' }}>
            <LinearGradient
              colors={['rgba(0,255,255,0.1)', 'rgba(0,255,255,0.1)']}
              style={{ height: 20 }}
            />
          </View>
        ),

      }}>
      <BottomTab.Screen
        name="Home"
        component={HomeScreen}
        options={{
          headerShown: false,
          title: '',
          tabBarIcon: ({ color }) => <Feather name="home" color={color} size={25} />,
        }}
      />
      <BottomTab.Screen
        name="Inbox"
        component={TabTwoScreen}
        options={{
          title: '',
          tabBarIcon: ({ color }) => <Feather name="inbox" color={color} size={25} />,
        }}
      />
      <BottomTab.Screen
        name="Favourite"
        component={TabTwoScreen}
        options={{
          title: '',
          tabBarIcon: ({ color }) => <Feather name="heart" color={color} size={25} />,
        }}
      />
      <BottomTab.Screen
        name="Settings"
        component={TabTwoScreen}
        options={{
          title: '',
          tabBarIcon: ({ color }) => <Feather name="user" color={color} size={25} />,
        }}
      />
    </BottomTab.Navigator>
  );
}

const styles = StyleSheet.create({
  container: {
    flex: 1,
    backgroundColor: '#e93b81',
    paddingTop: 50,
    paddingHorizontal: 12
  },
  column: {
    flexDirection: 'column',
    justifyContent: 'space-between',
    alignItems: 'center',
    marginBottom: 24
  },
  title: {
    fontSize: 24,
    fontWeight: '600',
    color: '#fff'
  },
  text: {
    fontSize: 16,
    fontWeight: 'normal',
    color: '#fff'
  },
});
