import React from 'react';
import { Image, StyleSheet, View } from 'react-native';
import { Card, Text } from 'react-native-elements';


export const PetSocialMediaCard = (petPostDataItem: any, navigation: any) => (
    <>
        {petPostDataItem.item.imagepath != "" &&
            <View style={styles.CardContainer}>

                <View style={styles.contentContainer}>

                    <Text style={styles.petname}>{petPostDataItem.item.petname}</Text>
                    <Text style={styles.petgender}>{petPostDataItem.item.petgender}  -  {petPostDataItem.item.petstages} </Text>

                    <View style={styles.askbuttonContainer}>
                    </View>

                </View>

                <Card>
                    <Card.Image
                        style={{ padding: 0 }}
                        source={{
                            uri: petPostDataItem.item.imagepath,
                        }}
                    />
                    <Text style={{ marginBottom: 10 }}>
                        {petPostDataItem.item.caption}
                    </Text>
                    <Text style={{ marginBottom: 10 }}>
                        {petPostDataItem.item.createDate}
                    </Text>
                </Card>
            </View >
        }
    </>

);
const styles = StyleSheet.create({
    CardContainer: {
        flex: 1,
        flexDirection: 'row',
        flexWrap: 'wrap',
        //alignItems: 'flex-start', // if you want to fill rows left to right
        borderRadius: 25,
        padding: 10,
        borderWidth: 1,
        borderColor: '#dddddd',
        marginVertical: 15,
    },
    contentContainer: {
        width: '50%',
        height: '100%',
        paddingLeft: 10,
        flex: 1,
    },
    petname: {
        fontWeight: 'bold',
        fontSize: 20
    },
    petgender: { fontSize: 10, marginBottom: 12, fontWeight: 'bold', color: '#888888' },
    desc: { fontSize: 10, color: '#999999' },
    imageitem: {
        aspectRatio: 0.95,
        width: '50%',
        borderRadius: 25,
    },
    askbutton: {
        backgroundColor: '#0b9298',
        borderRadius: 5,
        paddingHorizontal: 15
    },
    askbuttonContainer: {
        position: 'absolute',
        left: 10,
        bottom: 0,
    }

});
