import React, { useEffect, useState, useContext } from 'react';
import { StyleSheet, View, Image, TouchableOpacity, ScrollView, Platform, Pressable } from 'react-native';
import { Button, ButtonGroup, withTheme, Text, Input, Divider, Avatar } from 'react-native-elements';
import * as ImagePicker from 'expo-image-picker';
import * as FileSystem from 'expo-file-system';
import { FontAwesome } from '@expo/vector-icons';
import { storage, db, auth } from "../firebase";
import { ref as storage_ref, getDownloadURL, uploadBytes } from "firebase/storage";
import { collection, query, where, onSnapshot, getDocs, addDoc } from "firebase/firestore";

import { SettingStackProps, petPostDataProps } from '../types';
import Dropdown from '../components/dropdown'

export default function PostDetailScreen({ route, navigation }: { route: any, navigation: any }) {
  const { petItem } = route.params;

  return (
    <>

      <View style={styles.container}>
        <View style={styles.iconContainer}>
          <FontAwesome
            name="arrow-left"
            size={25}
            color="white"
            onPress={() => { navigation.navigate('Home') }}
          />
        </View>

        {petItem.imagepath != "" && <Image style={styles.imageitem}
          source={{ uri: petItem.imagepath }}
        />}
        <View style={styles.contentContainer}>
          <Text style={styles.petname}>{petItem.petname}</Text>
          <Text style={styles.petgender}>{petItem.petgender}  -  {petItem.petstages} </Text>
          <Text style={{ fontSize: 16, marginTop: 12, fontWeight: 'bold', }}>Pet Story</Text>
          <Text style={styles.desc}>{petItem.desc} </Text>

        </View>

        <View style={styles.footerContainer}>
          <View style={styles.ownerContainer}>
            <Avatar
              size={50}
              rounded
              source={{ uri: 'https://cdn.pixabay.com/photo/2019/11/03/20/11/portrait-4599553__340.jpg' }}
            />
            <View style={styles.ownerDetailContainer}>
              <Text style={{ fontWeight: 'bold', fontSize: 12 }}>Katie Burgess</Text>
              <Text style={{ fontSize: 10, color: '#aaaaaa' }}>owner</Text>
            </View>
          </View>
          <Button buttonStyle={styles.contactbutton}
            containerStyle={styles.contactbuttonContainer}
            titleStyle={{ fontWeight: 'bold', fontSize: 11 }}
            title="Contact me"
            onPress={() => {
              return navigation.navigate('PostDetail', { petItem: petItem });
            }}
          >
          </Button>
        </View>
      </View >
    </>
  );
}

const styles = StyleSheet.create({
  container: {
    flex: 1,
    alignItems: 'flex-start',
    justifyContent: 'flex-start',
    backgroundColor: 'white',
    height: "100%"
  },
  iconContainer: {
    padding: 20,
    alignSelf: 'flex-start',
    zIndex: 1,
    position: 'absolute'
  },
  imageitem: {
    zIndex: 0,
    aspectRatio: 0.95,
    width: '100%',
  },
  contentContainer: {
    width: '100%',
    height: '100%',
    padding: 30,
    flex: 1,
    borderRadius: 25,
    backgroundColor: 'white',
    top: -100
  },
  petname: {
    fontWeight: 'bold',
    fontSize: 30
  },
  petgender: {
    fontSize: 10, marginBottom: 12, fontWeight: 'bold', color: '#888888'
  },
  desc: {
    fontSize: 10, color: '#999999'
  },
  contactbutton: {
    backgroundColor: '#0b9298',
    borderRadius: 5,
    paddingHorizontal: 15
  },
  contactbuttonContainer: {
    marginHorizontal: 30
  },
  footerContainer: {
    position: 'absolute',
    flexDirection: 'row',
    justifyContent: 'space-between',
