import { StyleSheet, FlatList, ActivityIndicator, View } from 'react-native';
import { auth } from "../firebase";
import { RootTabScreenProps } from '../types';
import { Button, ButtonGroup, withTheme, Text, Input, Icon, SearchBar, Image } from 'react-native-elements';
import React, { useState } from 'react';
import { PetCard } from '../components/PetCard';
import { PetCatagory } from '../components/PetCatagory';
import { LinearGradient } from 'expo-linear-gradient';
import Constants from 'expo-constants'
const assetsObject = require('../assets/asset.tsx')
const petCatagoryData = [
    {
        id: '1',
        title: 'Cat',
        imagepath: 'petImage3',
    },
    {
        id: '2',
        title: 'Dog',
    },
    {
        id: '3',
        title: 'Bird',
    },
    {
        id: '4',
        title: 'Hamster',
    },
    {
        id: '5',
        title: 'Rabbit',
        imagepath: 'petImage1',
    },

];

export const PetCatagoryList = () => {
    return (<View style={styles.container}>
        {/* <PetCatagory item={petCatagoryData[0]} />
        <PetCatagory item={petCatagoryData[1]} />
        <PetCatagory item={petCatagoryData[2]} /> */}
        <View style={styles.imageitemContainer}>
            <Image style={styles.imageitem}
                source={require('../assets/images/petcatagory/cat.png')} />
            <Text style={{ fontWeight: 'bold' }}>Cat</Text>
        </View>
        <View style={styles.imageitemContainer}>
            <Image style={styles.imageitem}
                source={assetsObject['Cat']} />
            <Text style={{ fontWeight: 'bold' }}>Cat</Text>
        </View>
        <View style={styles.imageitemContainer}>
            <Image style={styles.imageitem}
                source={assetsObject['Cat']} />
            <Text style={{ fontWeight: 'bold' }}>Cat</Text>
        </View>
    </View>)
}

const styles = StyleSheet.create({
    container: {
        flex: 1,
        height: 100,
        justifyContent: 'space-between',
        flexDirection: 'row',
        position: 'relative'
    },
    imageitem: {
        aspectRatio: 1,
        width: "100%",
        borderRadius: 25
    },
    imageitemContainer: {
        width: "100%",
        flex: 1,
        margin: 5,
        alignItems: 'center',
        paddingBottom: 15,
    },
});
