import React, { useContext } from 'react';
import { StyleSheet, View, Pressable, Alert } from 'react-native';
import { createBottomTabNavigator } from '@react-navigation/bottom-tabs';
import { RootStackParamList, RootTabParamList, RootTabScreenProps } from '../types';
import HomeScreen from './HomeScreen';
import TabTwoScreen from './TabTwoScreen';
import SettingScreenNav from '../navigation/SettingScreenNav';
import { Feather } from '@expo/vector-icons';

export default function RootScreen({ navigation }: { navigation: any }) {
  const BottomTab = createBottomTabNavigator<RootTabParamList>();
  return (
    <BottomTab.Navigator
      initialRouteName="Home"
      screenOptions={{
        tabBarActiveTintColor: '#0b9298',
        tabBarShowLabel: true,
        tabBarStyle: { borderTopWidth: 0, backgroundColor: 'black', elevation: 0 }
      }}>
      <BottomTab.Screen
        name="Home"
        component={HomeScreen}
        options={{
          headerShown: false,
          tabBarIcon: ({ color }) => <Feather name="home" color={color} />,
        }}
        listeners={{
          tabPress: (e) => {
            e.preventDefault();
            Alert.alert('Alert Title', 'My Alert Msg', [
              {
                text: 'Cancel',
                onPress: () => console.log('Cancel Pressed'),
                style: 'cancel',
              },
              { text: 'OK', onPress: () => console.log('OK Pressed') },
            ]);

          },
        }}

      />
      <BottomTab.Screen
        name="Inbox"
        component={TabTwoScreen}
        options={{
          tabBarIcon: ({ color }) => <Feather name="inbox" color={color} />,
        }}
      />
      <BottomTab.Screen
        name="Favourite"
        component={TabTwoScreen}
        options={{
          tabBarIcon: ({ color }) => <Feather name="heart" color={color} />,
        }}
      />
      <BottomTab.Screen
        name="SettingRoot"
        component={SettingScreenNav}
        options={{
          headerShown: false,
          tabBarIcon: ({ color }) => <Feather name="user" color={color} />,
        }}
      />
    </BottomTab.Navigator>
  );
}
