import { StyleSheet } from 'react-native';
import { auth } from "../firebase";
import EditScreenInfo from '../components/EditScreenInfo';
import { View } from '../components/Themed';
import { RootTabScreenProps } from '../types';
import { Button, ButtonGroup, withTheme, Text, Input, Icon } from 'react-native-elements';

export default function TabOneScreen({ navigation }: RootTabScreenProps<'TabOne'>, itemId: string) {
  const handleSignOut = () => {

    auth
      .signOut()
      .then(() => {
        return navigation.replace('Login');
      })
      .catch((error: { message: any; }) => alert(error.message))
  }
  return (
    <View style={styles.container}>
      <View style={styles.headTextContainer}>
        <Text h2>Good Morning</Text><Text h4>Are you ready to find a new friend</Text>
      </View>
      <Text style={styles.title}>Tab One</Text>
      <Text style={styles.title}>{itemId}</Text>
      <View style={styles.separator} lightColor="#eee" darkColor="rgba(255,255,255,0.1)" />
      <Button title="Logout" onPress={handleSignOut}></Button>
    </View>
  );
}

const styles = StyleSheet.create({
  container: {
    flex: 1,
    alignItems: 'center',
    justifyContent: 'center',
  },
  title: {
    fontSize: 20,
    fontWeight: 'bold',
  },
  separator: {
    marginVertical: 30,
    height: 1,
    width: '80%',
  },
  headTextContainer: {
    overflow: 'hidden',
    flexDirection: 'column',
    justifyContent: 'space-between',
    paddingLeft: 35,
  }
});
