import React, { useState, useEffect } from 'react';
import { StyleSheet, View, Text, Image } from 'react-native';
import { Dropdown, MultiSelect } from 'react-native-element-dropdown';


const DropdownScreen = (props: any) => {
    const [dropdown, setDropdown] = useState(props.initvalue);
    const [selected, setSelected] = useState([]);
    useEffect(() => {
        setDropdown(props.initvalue)

    }, []);
    const _renderItem = (item: { label: boolean | React.ReactChild | React.ReactFragment | React.ReactPortal | null | undefined; }) => {
        return (
            <View style={styles.item}>
                <Text style={styles.textItem}>{item.label}</Text>
                {/* <Image style={styles.icon} source={require('../assets/images/icon.png')} /> */}
            </View>
        );
    };

    return (
        <View style={styles.container}>
            <Dropdown
                style={styles.dropdown}
                containerStyle={styles.shadow}
                data={props.data}
                labelField="label"
                valueField="value"
                placeholder={props.initvalue ? props.initvalue : 'Select a value'}
                value={dropdown}
                maxHeight={112}
                onChange={item => {
                    setDropdown(item.value);
                    props.setValue(item.value)
                }}
                renderItem={item => _renderItem(item)}
            // renderLeftIcon={() => (
            //     <Image style={styles.icon} source={require('../assets/images/icon.png')} />
            // )}
            />
        </View>
    );
};

export default DropdownScreen;

const styles = StyleSheet.create({
    container: {
        backgroundColor: 'white',
    },
    dropdown: {
        backgroundColor: 'white',
        borderBottomColor: '#aaaaaa',
        borderBottomWidth: 0.5,

    },
    icon: {
        marginRight: 5,
        width: 18,
        height: 18,
    },
    item: {
        // paddingVertical: 17,
        paddingHorizontal: 4,
        flexDirection: 'row',
        justifyContent: 'space-between',
        alignItems: 'center',
    },
    textItem: {
        fontSize: 16,
    },
    shadow: {
        shadowColor: '#aaaaaa',
        shadowOffset: {
            width: 0,
            height: 1,
        },
        shadowOpacity: 0.2,
        shadowRadius: 1.41,
        elevation: 2,
    },
});
