import { PetCard } from '../components/PetCard';
import { StyleSheet, FlatList, ActivityIndicator, View } from 'react-native';
import { ref, getDownloadURL } from "firebase/storage";
import { collection, query, where, onSnapshot, getDocs, orderBy } from "firebase/firestore";
import React, { useEffect, useState } from 'react';
import { SettingStackProps, petPostDataProps } from '../types';
import { storage, db } from "../firebase";
import { LinearGradient } from 'expo-linear-gradient';
export function PetCardFlatList() {
    const [petPostDataArray, setpetPostDataArray] = useState<petPostDataProps[]>([]);
    const [isLoading, setIsLoading] = useState(true);

    const getData = async () => {


        const q = query(collection(db, "PetPost"), orderBy('createdate', "desc"));
        const unsubscribe = onSnapshot(q, (querySnapshot) => {
            //setpetPostDataArray([])
            querySnapshot.forEach((doc) => {
                getDownloadURL(ref(storage, doc.data()['imagepath']))
                    .then((url) => {
                        console.log("petname:" + doc.data()['petname'] + " " + format_timestamp(doc.data()['createdate']))
                        const newjson = {
                            id: doc.id,
                            imagepath: url,
                            petname: doc.data()['petname'],
                            petgender: doc.data()['petgender'],
                            petstages: doc.data()['petstages'],
                            desc: doc.data()['desc']
                        }
                        //console.log(doc.data()['createdate'], newjson);
                        setpetPostDataArray(oldArray => [...oldArray, newjson]);
                    })
                    .catch((error) => {
                    });
            });
        });

    }

    useEffect(() => {
        getData()
        setIsLoading(false)
    }, []);

    return (
        <>
            {isLoading && <ActivityIndicator size="large" color='#298e96' />}
            <View style={styles.uppergadient}>
                <LinearGradient
                    colors={['rgba(255,255,255,1)', 'rgba(0,255,255,0)']}
                    style={{ height: 40 }}
                />
            </View>
            <FlatList
                showsVerticalScrollIndicator={false}
                showsHorizontalScrollIndicator={false}
                data={petPostDataArray}
                renderItem={PetCard}
                keyExtractor={item => item.id}
            />
            <View style={styles.lowergadient}>
                <LinearGradient
                    colors={['rgba(255,255,255,0)', 'rgba(255,255,255,1)']}
                    style={{ height: 40 }}
                />
            </View>

        </>

    );
}


const styles = StyleSheet.create({
    uppergadient: {
        flex: 1,
        backgroundColor: 'transparent',
        top: 0,
        width: '100%',
        height: 40,
        zIndex: 10
    },
    lowergadient: {
        flex: 1,
        backgroundColor: 'transparent',
        position: 'absolute',
        bottom: 0,
        width: '100%',
        height: 40
    }
});

function format_timestamp(timestamp: number) {
    var t = new Date(timestamp);
    var hours = t.getHours();
    var minutes = t.getMinutes();
    var newformat = t.getHours() >= 12 ? 'PM' : 'AM';

    // Find current hour in AM-PM Format 
    hours = hours % 12;

    // To display "0" as "12" 
    hours = hours ? hours : 12;
    var formatted =
        (t.toString().split(' ')[0])
        + ', ' + ('0' + t.getDate()).slice(-2)
        + '/' + ('0' + (t.getMonth() + 1)).slice(-2)
        + '/' + (t.getFullYear())
        + ' - ' + ('0' + t.getHours()).slice(-2)
        + ':' + ('0' + t.getMinutes()).slice(-2)
        + ' ' + newformat;
}
