import { StyleSheet, View, Pressable } from 'react-native';
import { auth } from "../firebase";
import { FontAwesome } from '@expo/vector-icons';
import EditScreenInfo from '../components/EditScreenInfo';
import { Button, ButtonGroup, withTheme, Text, Input } from 'react-native-elements';
import { SettingStackParamList, RootTabParamList, SettingStackProps } from '../types';
import { createNativeStackNavigator } from '@react-navigation/native-stack';
import SettingScreen from '../screens/SettingScreen';
import CreatePostScreen from '../screens/CreatePostScreen';
import ViewMyPostScreen from '../screens/ViewMyPostScreen';
import UserSettingScreen from '../screens/UserSettingScreen';
const Stack = createNativeStackNavigator<SettingStackParamList>();

export default function SettingsScreen({ navigation }: SettingStackProps<'Setting'>) {
  return (
    <Stack.Navigator>
      <Stack.Screen options={{ headerShown: false }} name="Setting" component={SettingScreen}
      ></Stack.Screen>
      <Stack.Screen name="UserSetting" component={UserSettingScreen}
        options={{
          title: 'UserSetting',
          headerLeft: () => (
            <Pressable
              onPress={() => navigation.navigate('UserSetting')}
              style={({ pressed }) => ({
                opacity: pressed ? 0.5 : 1,
                marginLeft: 20
              })}>
              <FontAwesome
                name="arrow-left"
                size={25}
                color="black"
                style={{ marginRight: 15 }}
              />
            </Pressable>
          ),
        }}
      ></Stack.Screen>
      <Stack.Screen name="CreatePost" component={CreatePostScreen}
        options={{
          title: 'Create Pet Adoption Post',
          headerLeft: () => (
            <Pressable
              onPress={() => navigation.navigate('Setting')}
              style={({ pressed }) => ({
                opacity: pressed ? 0.5 : 1,
                marginLeft: 20
              })}>
              <FontAwesome
                name="arrow-left"
                size={25}
                color="black"
                style={{ marginRight: 15 }}
              />
            </Pressable>
          ),
        }}
      ></Stack.Screen>
      <Stack.Screen name="ViewMyPost" component={ViewMyPostScreen}
        options={{
          title: 'View all my pet posts',
          headerLeft: () => (
            <Pressable
              onPress={() => navigation.navigate('Setting')}
              style={({ pressed }) => ({
                opacity: pressed ? 0.5 : 1,
                marginLeft: 20
              })}>
              <FontAwesome
                name="arrow-left"
                size={25}
                color="black"
                style={{ marginRight: 15 }}
              />
            </Pressable>
          ),
        }}
      ></Stack.Screen>
    </Stack.Navigator>
  );
}

const styles = StyleSheet.create({
  container: {
    flex: 1,
    alignItems: 'center',
    justifyContent: 'center',
  },
  title: {
    fontSize: 20,
    fontWeight: 'bold',
  },
  separator: {
    marginVertical: 30,
    height: 1,
    width: '80%',
  },
});
