import React, { useEffect, useState, useContext } from 'react';
import { StyleSheet, View, Image, Alert, ScrollView } from 'react-native';
import { Button, ButtonGroup, withTheme, Text, Input, Divider, Avatar } from 'react-native-elements';
import * as ImagePicker from 'expo-image-picker';
import * as FileSystem from 'expo-file-system';
import { FontAwesome } from '@expo/vector-icons';
import { storage, db, auth } from "../firebase";
import { ref as storage_ref, getDownloadURL, uploadBytes } from "firebase/storage";
import { collection, query, where, onSnapshot, getDocs, addDoc, doc, setDoc } from "firebase/firestore";

import { SettingStackProps, petPostDataProps } from '../types';
import Dropdown from '../components/dropdown'

export default function PetSocialMediaScreen({ route, navigation }: { route: any, navigation: any }) {
  const [ownerName, setOwnerName] = useState<string | undefined>('');
  const [ownerImagepath, setOwnerImage] = useState("");

  const [petPostDataItem, setPetPostDataItem] = useState(route.params.petPostDataItem.item)

  const getOwnerDetail = async () => {
    if (!petPostDataItem.postcreator) return false;

    const unsub = onSnapshot(doc(db, "Users", petPostDataItem.postcreator), (doc) => {
      if (doc == undefined) return false;
      if (!doc.data()) return false;
      setOwnerName(doc.data()?.username)
      setOwnerImage(doc.data()?.userIconUri)
    });
  }

  const getCurrentUserDetail = async () => {
    if (!petPostDataItem.postcreator) return false;
    var userId = auth.currentUser?.uid;
    if (userId) {
      const unsub = onSnapshot(doc(db, "Users", userId), (doc) => {
        if (doc == undefined) return false;
        if (!doc.data()) return false;
        setAddressDocUri(doc.data()?.addressDocUri);
        setIncomeDocUri(doc.data()?.incomeDocUri);
        setTotalIncome(doc.data()?.totalIncome);
        setApartmentType(doc.data()?.apartmentType);
        setApartmentSize(doc.data()?.apartmentSize);
        setHvExperience(doc.data()?.hvExperience);
        setHvWindowScreen(doc.data()?.hvWindowScreen);
        setNumOfOtherPet(doc.data()?.numOfOtherPet);
        setOtherPetDescription(doc.data()?.otherPetDescription);
        setHvSmoker(doc.data()?.hvSmoker);
        setHvAllergySymptoms(doc.data()?.hvAllergySymptoms);
        setHvRespiratoryDiseases(doc.data()?.hvRespiratoryDiseases);
      });
    }

  }


  useEffect(() => {
    getCurrentUserDetail();
    getOwnerDetail();
    navigation.getParent()?.setOptions({ tabBarStyle: { display: "none" } });
    return () => navigation.getParent()?.setOptions(
      { tabBarStyle: { borderTopWidth: 0, backgroundColor: 'white', elevation: 0, height: '8%' } });
  }, [navigation]);

  return (
    <>
      <View style={styles.container}>
        <View style={styles.iconContainer}>
          <FontAwesome
            name="arrow-left"
            size={25}
            color="white"
            onPress={() => { navigation.navigate('Home') }}
          />
        </View>
        <View style={styles.iconContainer2}>
          <FontAwesome
            name="heart-o"
            size={25}
            color="white"
          />
        </View>
        {petPostDataItem.imagepath != "" && <Image style={styles.imageitem}
          source={{ uri: petPostDataItem.imagepath }}
        />}
        <View style={styles.contentContainer}>
          <Text style={styles.petname}>{petPostDataItem.petname}</Text>
          <Text style={styles.petgender}>{petPostDataItem.petgender}  -  {petPostDataItem.petstages} </Text>



        </View>

      </View >
    </>
  );
}

const styles = StyleSheet.create({
  container: {
    flex: 1,
    alignItems: 'flex-start',
    justifyContent: 'flex-start',
    backgroundColor: 'white',
    height: "100%"
  },
  iconContainer: {
    marginTop: 40,
    padding: 20,
    alignSelf: 'flex-start',
    zIndex: 1,
    position: 'absolute'
  },
  iconContainer2: {
    marginTop: 40,
    padding: 20,
    alignSelf: 'flex-end',
    zIndex: 1,
    position: 'absolute'
  },
  imageitem: {
    zIndex: 0,
    aspectRatio: 1.1,
    width: '100%',
  },
  contentContainer: {
    width: '100%',
    height: '100%',
    padding: 30,

    flex: 1,
    borderRadius: 25,
    backgroundColor: 'white',
    top: -30
  },
  petname: {
    fontWeight: 'bold',
    fontSize: 30
  },
  petgender: {
    fontSize: 12, marginBottom: 12, fontWeight: 'bold', color: '#888888'
  },
  paragraph: {
    fontSize: 12, color: '#999999'
  },
  required: {
    fontSize: 12, color: 'red'
  },
  fulfilled: {
    fontSize: 12, color: 'green'
  },
  paragraphTitle: {
    fontSize: 12, color: '#999999', fontWeight: 'bold'
  },
  contactbutton: {
    backgroundColor: '#0b9298',
    borderRadius: 5,
    paddingHorizontal: 15,
    marginRight: 30
  },
  contactbuttonContainer: {
    marginHorizontal: 30
  },
  footerContainer: {
    position: 'absolute',
    flexDirection: 'row',
    justifyContent: 'space-between',
    alignItems: 'center',
    bottom: 0,
    width: '100%',
    height: '10%',
  },
  ownerContainer: {
    marginLeft: 30,
    flexDirection: 'row',
    justifyContent: 'flex-start',
  },
  ownerDetailContainer: {
    marginLeft: 10,
    flexDirection: 'column',
    justifyContent: 'center',
    alignItems: 'flex-start'
  },


});
