import { StyleSheet, View, Image } from 'react-native';
import { auth } from "../firebase";
import EditScreenInfo from '../components/EditScreenInfo';
import { SettingStackProps, petPostDataProps } from '../types';
import { Button, ButtonGroup, withTheme, Text, Input } from 'react-native-elements';
import { storage, db } from "../firebase";
import React, { useEffect, useState } from 'react';
import { ref, getDownloadURL } from "firebase/storage";
import { collection, query, where, onSnapshot, getDocs } from "firebase/firestore";


export default function CreatePostScreen({ navigation }: SettingStackProps<'Setting'>) {
  const [imageUrl, setImageUrl] = useState("");
  const [petPostDataArray, setpetPostDataArray] = useState<petPostDataProps[]>([]);
  //gs://petsaver-1fa7d.appspot.com/petImages/cat.png

  const getData = async () => {
    const querySnapshot = await getDocs(collection(db, "PetPost"));
    querySnapshot.forEach((doc) => {
      console.log(doc.id, " => ", doc.data());
      getDownloadURL(ref(storage, doc.data()['imagepath']))
        .then((url) => {
          const newjson = {
            imagepath: url,
            petname: doc.data()['petname'],
            petgender: doc.data()['petgender'],
            petstages: doc.data()['petstages'],
            desc: doc.data()['desc']
          }
          setpetPostDataArray(oldArray => [...oldArray, newjson]);

        })
        .catch((error) => {
          // Handle any errors
        });

    });

  }

  useEffect(() => {
    getData()
  }, []);



  const q = query(collection(db, "PetPost"));



  return (
    <View style={styles.container}>
      <Text style={styles.title}>CreatePostScreen</Text>
      <View style={styles.separator} />
      <Input style={styles.input} placeholder='Email' />
      <View style={styles.CardContainer}>
        <Image style={styles.imageitem}
          source={{ uri: imageUrl }}
        />
      </View>
    </View>
  );
}

const styles = StyleSheet.create({
  container: {
    flex: 1,
    alignItems: 'center',
    justifyContent: 'center',
  },
  title: {
    fontSize: 20,
    fontWeight: 'bold',
  },
  separator: {
    marginVertical: 30,
    height: 1,
    width: '80%',
  },
  input: {
    fontSize: 12
  },
  CardContainer: {
    flex: 1,
    flexDirection: 'row',
    flexWrap: 'wrap',
    alignItems: 'flex-start', // if you want to fill rows left to right
    borderRadius: 25,
    padding: 10,
    borderWidth: 1,
    borderColor: '#dddddd',
    marginVertical: 15,
  },
  imageitem: {
    aspectRatio: 0.95,
    width: 100,
    borderRadius: 25,
  },
});
