import React, { useEffect, useState, useContext } from 'react';
import { StyleSheet, View, Image, TouchableOpacity, ScrollView, Platform, Pressable, Alert } from 'react-native';
import { Button, ButtonGroup, withTheme, Text, Input, Divider, Avatar, ListItem, Icon } from 'react-native-elements';
import * as ImagePicker from 'expo-image-picker';
import * as FileSystem from 'expo-file-system';
import { FontAwesome } from '@expo/vector-icons';
import { storage, db, auth } from "../firebase";
import { ref as storage_ref, getDownloadURL, uploadBytes } from "firebase/storage";
import { collection, query, where, onSnapshot, getDocs, addDoc, doc, setDoc, updateDoc, deleteField } from "firebase/firestore";

import { SettingStackProps, petPostDataProps } from '../types';
import Dropdown from '../components/dropdown';
const assetsObject = require('../assets/asset.tsx')

export default function PetRequestDetailScreen({ route, navigation }: { route: any, navigation: any }) {
  const { petPostDataItem } = route.params;
  const [ownerName, setOwnerName] = useState<string | undefined>('');
  const [ownerImagepath, setOwnerImage] = useState("");
  const [requestUserList, setRequstUserList] = useState<any>([])
  const [selectedButtonIndex, setSelectedButtonIndex] = useState<any>([]);

  const getRequestDetail = async (requestID: string) => {

    if (!requestID) return false;

    const unsub = onSnapshot(doc(db, "Users", requestID), (doc) => {
      if (doc == undefined) return false;
      if (!doc.data()) return false;

      const newjson = {
        userId: doc.id,
        userName: doc.data()?.username,
        userImage: doc.data()?.imagepath,
        userDesc: doc.data()?.desc,
        userEmail: doc.data()?.email,
        userAge: doc.data()?.userAge,
      }
      setRequstUserList((oldArray: any) => [...oldArray, newjson]);
    });
  }

  const removePetAdopter = async (swipeableIndex: number) => {
    var requestUserID = requestUserList[swipeableIndex].userId
    try {
      if (!requestUserID || !petPostDataItem.item.id) {
        console.error("requestUserID or petPostDataItem.id not found:  ", requestUserID, petPostDataItem.item.id);
        return false;
      }
      if (requestUserID != petPostDataItem.item.adopterID) {
        console.log('You can only reject current adoptor')
        Alert.alert(
          "Request Failed", "You can set petowner to adopter",
          [
            { text: "OK", onPress: () => console.log("OK Pressed") }
          ]
        );
        return false;
      }
      const petPostRef = doc(db, 'PetPost', petPostDataItem.item.id);
      await updateDoc(petPostRef, {
        adopterID: deleteField()
      });
      console.log("adopterID deleteField for post: ", petPostRef.id);

    } catch (e) {
      console.error("Error adding document:  ", e);
    }
  }

  const setPetAdopter = async (swipeableIndex: number) => {
    var requestUserID = requestUserList[swipeableIndex].userId
    try {
      if (!requestUserID || !petPostDataItem.item.id) {
        console.error("requestUserID or petPostDataItem.id not found:  ", requestUserID, petPostDataItem.item.id);
        return false;
      }

      if (requestUserID == petPostDataItem.item.postcreator) {
        console.log('You cannot set petowner to adopter')
        Alert.alert(
          "Request Failed", "You cannot set petowner to adopter",
          [
            { text: "OK", onPress: () => console.log("OK Pressed") }
          ]
        );
        return false;
      }

      const petPostRef = doc(db, 'PetPost', petPostDataItem.item.id);
      await updateDoc(petPostRef, {
        adopterID: requestUserID
      });

      console.log("adopterID written as ID: ", requestUserID);

    } catch (e) {
      console.error("Error adding document:   ", e);
    }
  }

  useEffect(() => {
    setRequstUserList([])
    console.log("assetsObject:", require('../assets/images/icon.png'))
    console.log("assetsObject:", require('../assets/images/petcatagory/rabbit.jpg'))
    console.log("petPostDataItem:", petPostDataItem.item.requestUsers)
    if (petPostDataItem.item.requestUsers) {
      petPostDataItem.item.requestUsers.forEach((requestID: string, index: any) => {
        getRequestDetail(requestID);
      })
    }
  }, []);

  return (
    <>
      <View style={styles.container}>
        <Text></Text>
        <View >
          {
            requestUserList.map((l: any, i: number) => (
              <ListItem.Swipeable key={i} bottomDivider
                containerStyle={{
                  backgroundColor: (requestUserList[i].userId == petPostDataItem.item.adopterID
                    && petPostDataItem.item.adopterID) ? '#CBFFC0' : '#dddddd'
                }}
                leftContent={
                  <Button
                    title="Reject"
                    icon={{ name: 'close', color: 'white' }}
                    buttonStyle={{ height: '100%', backgroundColor: 'red' }}
                    onPress={() => {
                      console.log('removePetAdopter', i)
                      removePetAdopter(i)
                    }
                    }
                  />
                }
                leftStyle={{ height: '100%', backgroundColor: 'red', justifyContent: 'center' }}
                rightContent={
                  <Button
                    title="Accpet"
                    icon={{ name: 'check', color: 'white' }}
                    buttonStyle={{ minHeight: '100%', backgroundColor: 'green' }}
                    onPress={() => {
                      console.log('setPetAdopter', i)
                      setPetAdopter(i)
                    }
                    }
                  />
                }
                rightStyle={{ height: '100%', backgroundColor: 'green', justifyContent: 'center' }}

              >
                <Avatar source={{ uri: l.userImage ? l.userImage : "" }} rounded />
                <ListItem.Content>
                  <ListItem.Title>{l.userEmail} </ListItem.Title>
                  <ListItem.Subtitle>User Name: {l.userName}  {l.userId}</ListItem.Subtitle>
                  <ListItem.Subtitle>user Description:{l.userDesc}</ListItem.Subtitle>
                </ListItem.Content>
              </ListItem.Swipeable>
            ))
          }
        </View>
      </View >
    </>
  );
}

const styles = StyleSheet.create({
  container: {
    // flex: 1,
    // alignItems: 'flex-start',
    // justifyContent: 'flex-start',
    backgroundColor: 'white',
    height: "100%"
  },
  iconContainer: {
    padding: 20,
    alignSelf: 'flex-start',
    zIndex: 1,
    position: 'absolute'
  },
  iconContainer2: {
    padding: 20,
    alignSelf: 'flex-end',
    zIndex: 1,
    position: 'absolute'
  },
  imageitem: {
    zIndex: 0,
    aspectRatio: 1.1,
    width: '100%',
  },
  contentContainer: {
    width: '100%',
    height: '100%',
    padding: 30,
    flex: 1,
    borderRadius: 25,
    backgroundColor: 'white',
    top: -30
  },
  petname: {
    fontWeight: 'bold',
    fontSize: 30
  },
  petgender: {
    fontSize: 10, marginBottom: 12, fontWeight: 'bold', color: '#888888'
  },
  desc: {
    fontSize: 10, color: '#999999'
  },
  contactbutton: {
    backgroundColor: '#0b9298',
    borderRadius: 5,
    paddingHorizontal: 15,
    marginRight: 30
  },
  contactbuttonContainer: {
    marginHorizontal: 30
  },
  footerContainer: {
    position: 'absolute',
    flexDirection: 'row',
    justifyContent: 'space-between',
    alignItems: 'center',
    bottom: 0,
    width: '100%',
    height: '10%',
  },
  ownerContainer: {
    marginLeft: 30,
    flexDirection: 'row',
    justifyContent: 'flex-start',
  },
  ownerDetailContainer: {
    marginLeft: 10,
    flexDirection: 'column',
    justifyContent: 'center',
    alignItems: 'flex-start'
  },


});

const blankIconUrl = 'https://firebasestorage.googleapis.com/v0/b/petsaver-1fa7d.appspot.com/o/userIcon%adaptive-icon.png?alt=media&token=https://firebasestorage.googleapis.com/v0/b/petsaver-1fa7d.appspot.com/o/userIcon%2Fadaptive-icon.png?alt=media&token=46b9f84d-6f72-4020-8e8d-e6155fa95896'
