import React from 'react';
import { Image, StyleSheet, View } from 'react-native';
import { Card, Text } from 'react-native-elements';


export const PetSocialMediaCard = (petPostDataItem: any, navigation: any) => (
    <View style={styles.CardContainer}>
        {
            petPostDataItem.item.imagepath != "" &&
            <View >
                <Image style={styles.imageitem}
                    source={{ uri: petPostDataItem.item.imagepath }}
                />
                <Text style={{ marginBottom: 10 }}>
                    {petPostDataItem.item.caption}
                </Text>
                <Text style={{ marginBottom: 10 }}>
                    {timestampToDate(petPostDataItem.item.createdate.seconds)}
                </Text>
            </View >
        }
    </View >
);
const styles = StyleSheet.create({
    CardContainer: {
        width: '33%',
        borderColor: '#fefefe',
        borderWidth: 1
    },
    imageitem: {
        aspectRatio: 1,
    },
});

