import React, { useEffect, useState, useContext } from 'react';
import { StyleSheet, View, Image, TouchableOpacity, ScrollView, Platform } from 'react-native';
import { Picker } from '@react-native-community/picker';
import { Button, ButtonGroup, withTheme, Text, Input, Divider } from 'react-native-elements';
import * as ImagePicker from 'expo-image-picker';
import * as FileSystem from 'expo-file-system';

import { storage, db, auth } from "../firebase";
import { ref as storage_ref, getDownloadURL, uploadBytes } from "firebase/storage";
import { collection, query, where, onSnapshot, getDocs, addDoc } from "firebase/firestore";

import { SettingStackProps, petPostDataProps } from '../types';

export default function CreatePostScreen() {
  const [imagepath, setImage] = useState("");
  const [downloadURL, setDownloadURL] = useState("");
  const [petGender, setGenderValue] = useState<number | string>("Male");
  const [petStage, setStageValue] = useState<number | string>("Kid");
  const [petName, setPetName] = useState('');
  const [petAge, setPetAge] = useState<number | string>('');
  const [petDescription, setPetDescription] = useState('');
  const [uploading, setUploading] = useState(false);
  const [transferred, setTransferred] = useState(0);
  const [selectedButtonIndex, setSelectedButtonIndex] = useState(2);

  const pickImageByLibrary = async () => {
    // No permissions request is necessary for launching the image library
    let result = await ImagePicker.launchImageLibraryAsync({
      mediaTypes: ImagePicker.MediaTypeOptions.All,
      allowsEditing: true,
      aspect: [4, 3],
      quality: 1,
    });
    if (!result.cancelled) {
      setImage(result.uri);
    }
  };
  const pickImageByCamera = async () => {
    // No permissions request is necessary for launching the image library
    let result = await ImagePicker.launchCameraAsync({
      mediaTypes: ImagePicker.MediaTypeOptions.All,
      allowsEditing: true,
      aspect: [4, 3],
      quality: 1,
    });
    if (!result.cancelled) {
      setImage(result.uri);
    }
  };

  const uploadImage = async () => {
    setUploading(true)

    const userId = auth.currentUser?.uid;
    console.log('userId: ', userId);
    const imagename = Date.now() + '-' + getRandomString(5) + '-' + userId
    const storageRef = storage_ref(storage, 'petImages/' + imagename + '.png');

    const blob: Blob = await new Promise((resolve, reject) => {
      const xhr = new XMLHttpRequest();
      xhr.onload = function () {
        resolve(xhr.response);
      };
      xhr.onerror = function () {
        reject(new TypeError("Network request failed"));
      };
      xhr.responseType = "blob";
      xhr.open("GET", imagepath, true);
      xhr.send(null);
    });
    const snapshot = await uploadBytes(storageRef, blob);
    const snapshotDownloadURL = await getDownloadURL(snapshot.ref);
    setDownloadURL(snapshotDownloadURL);
    console.log('File available at', snapshotDownloadURL);

    try {
      const docRef = await addDoc(collection(db, "PetPost"), {
        imagepath: snapshotDownloadURL,
        petname: petName,
        petgender: petGender,
        petstages: petStage,
        desc: petDescription,
        createdate: new Date()
      });
      console.log("Document written with ID: ", docRef.id);
    } catch (e) {
      console.error("Error adding document:  ", e);
    }

    setUploading(false)
  }

  return (
    <>
      <View style={styles.container}>
        <ScrollView showsVerticalScrollIndicator={false}
          showsHorizontalScrollIndicator={false}
          style={{ height: "100%", width: "100%" }}>
          <Input style={styles.input} placeholder="Charlie" label='Pet Name:' onChangeText={text => setPetName(text)} />
          <Input style={styles.input} placeholder="0-100" label='Pet Age:' onChangeText={text => setPetAge(text)} />
          <View style={styles.pickerContainer}>
            <Text style={styles.pickerLabel} >Pet Stages:</Text>
            <Picker
              selectedValue={petGender}
              style={{ height: 50, width: 100, borderColor: 'white', fontSize: 12, color: 'rgb(36, 36, 36)' }}
              itemStyle={{ backgroundColor: "grey", color: "blue", fontSize: 12 }}
              onValueChange={(itemValue, itemIndex) => {
                setStageValue(itemValue)
              }}>
              <Picker.Item label="Baby" value="baby" />
              <Picker.Item label="Kid" value="kid" />
              <Picker.Item label="Adult" value="adult" />
            </Picker>
          </View>
          <Input style={styles.input} placeholder="Descript your pet here" label='Pet Description:' onChangeText={text => setPetDescription(text)} />
          <View style={styles.pickerContainer}>
            <Text style={styles.pickerLabel} >Pet Gender:</Text>
            <Picker
              selectedValue={petGender}
              style={{ height: 50, width: 100, borderColor: 'white', fontSize: 12, color: 'rgb(36, 36, 36)' }}
              itemStyle={{ backgroundColor: "grey", color: "blue", fontSize: 12 }}
              onValueChange={(itemValue, itemIndex) => {
                setGenderValue(itemValue)
              }}>
              <Picker.Item label="Male" value="male" />
              <Picker.Item label="Female" value="female" />
            </Picker>
          </View>
          <View style={styles.imagePickerContainer}>
            <Text style={styles.pickerLabel} >Pet Image: </Text>
            <View style={styles.paddingView} />
            <ButtonGroup
              buttons={['Select an image', 'Open camera']}
              selectedIndex={selectedButtonIndex}
              onPress={(value) => {
                setSelectedButtonIndex(value);
                if (value == 0) {
                  pickImageByLibrary()
                }
                if (value == 1) {
                  pickImageByCamera()
                }
              }}
              containerStyle={{ marginBottom: 20 }}
            />
            <></>
            {imagepath != "" &&
              <TouchableOpacity onPress={pickImageByLibrary}>
                <Image style={styles.imageitem}
                  source={{ uri: imagepath }}
                />
              </TouchableOpacity>
            }
          </View>
          <Button title="Submit" onPress={uploadImage}
            loading={uploading}
            buttonStyle={{
              backgroundColor: 'rgba(111, 202, 186, 1)',
              borderRadius: 5,
              margin: 10, marginTop: 30
            }}

            titleStyle={{ fontSize: 12 }} />
        </ScrollView>
      </View>
    </>
    //imagepath={imagepath} petname={petName} petgender={petGender} petstages={petStage} desc={petDescription} 
  );
}

const styles = StyleSheet.create({
  container: {
    flex: 1,
    alignItems: 'flex-start',
    justifyContent: 'flex-start',
    padding: 20,
    backgroundColor: 'white',
    height: "100%"
  },
  title: {
    fontSize: 20,
    fontWeight: 'bold',
  },
  separator: {
    marginVertical: 30,
    height: 1,
    width: '80%',
  },
  input: {
    fontSize: 12
  },
  pickerContainer: {
    paddingHorizontal: 10,

  },
  pickerLabel: {
    fontSize: 16,
    fontWeight: 'bold',
    color: 'rgb(134, 147, 158)'
  },
  CardContainer: {
    width: '100%',
    flex: 1,
    flexDirection: 'row',
    flexWrap: 'wrap',
    alignItems: 'flex-start', // if you want to fill rows left to right
    borderRadius: 25,
    padding: 10,
    borderWidth: 1,
    borderColor: '#dddddd',
    marginVertical: 15,
  },
  imageitem: {
    aspectRatio: 0.95,
    width: 200,
    borderRadius: 25,
    fontSize: 12
  },
  contentContainer: {
    width: '50%',
    height: '100%',
    paddingLeft: 10,
    flex: 1,
  },
  petname: {
    fontWeight: 'bold',
    fontSize: 20
  },
  petgender: {
    fontSize: 10,
    marginBottom: 12,
    fontWeight: 'bold',
    color: '#888888'
  },
  desc: {
    fontSize: 10,
    color: '#999999'
  },
  askbutton: {
    backgroundColor: '#0b9298',
    borderRadius: 5,
    paddingHorizontal: 15
  },
  askbuttonContainer: {
    position: 'absolute',
    left: 10,
    bottom: 0,
  },
  imagePickerContainer: {
    flexDirection: 'column',
    paddingHorizontal: 10
  },
  paddingView: {
    paddingTop: 10
  }
});


function getRandomString(length: number) {
  var randomChars = 'ABCDEFGHIJKLMNOPQRSTUVWXYZabcdefghijklmnopqrstuvwxyz0123456789';
  var result = '';
  for (var i = 0; i < length; i++) {
    result += randomChars.charAt(Math.floor(Math.random() * randomChars.length));
  }
  return result;
}
