import * as WebBrowser from 'expo-web-browser';
import { ReactChild, ReactFragment, ReactPortal } from 'react';
import { StyleSheet, FlatList, ActivityIndicator, View, Image } from 'react-native';

import { Button, ButtonGroup, withTheme, Text, Input, Icon, SearchBar } from 'react-native-elements';

type imageOptions = {
    [key: string]: any
}
const assetsObject: imageOptions = {
    petImage1: require('../assets/images/cat.png'),
    petImage2: require('../assets/images/dog.jpeg'),
    petImage3: require('../assets/images/cat2.jpg'),
}

export const PetCard = ({ item }: any) => (
    <View style={styles.CardContainer}>
        <Image style={styles.imageitem}
            source={assetsObject[item.imagepath]}
        />
        <View style={styles.contentContainer}>
            <Text style={styles.petname}>{item.petname}</Text>
            <Text style={styles.petgender}>{item.petgender} - {item.petstages} </Text>
            <Text style={styles.desc}>{item.desc} </Text>
        </View>
    </View>
);


const styles = StyleSheet.create({
    CardContainer: {
        flex: 1,
        flexDirection: 'row',
        flexWrap: 'wrap',
        alignItems: 'flex-start', // if you want to fill rows left to right
        borderRadius: 25,
        padding: 10,
        borderWidth: 1,
        borderColor: '#aaaaaa',
        marginVertical: 20
    },
    contentContainer: {
        width: '60%',
        paddingleft: 10,
        flex: 1,
    },
    petname: {
        fontWeight: 'bold',
        fontSize: 30
    },
    petgender: { fontSize: 12, marginBottom: 12 },
    desc: { fontSize: 12, color: '#aaaaaa' },
    imageitem: {
        aspectRatio: 1,
        width: '40%',
        borderRadius: 25,
    }

});
