import { StatusBar } from 'expo-status-bar';
import { SafeAreaProvider } from 'react-native-safe-area-context';

import useCachedResources from './hooks/useCachedResources';
import useColorScheme from './hooks/useColorScheme';
import Navigation from './navigation';

import React, { useState } from 'react';
import { Platform, StyleSheet, View, Text, Image, LogBox } from 'react-native';
import AppIntroSlider from 'react-native-app-intro-slider';
import { NavigationContainer } from '@react-navigation/native';
import { createNativeStackNavigator } from '@react-navigation/native-stack';
import LinkingConfiguration from './navigation/LinkingConfiguration';
import LoginScreen from './screens/LoginScreen';
import RootScreen from './screens/RootScreen';

LogBox.ignoreLogs(['']);

const Stack = createNativeStackNavigator();

function checkIsWeb() {
  //return (Platform.OS === 'ios' || Platform.OS === 'android') ? false : true;
  return true //for dev testing 
}


export default function App() {
  const isLoadingComplete = useCachedResources();
  const colorScheme = useColorScheme();
  const [showRealApp, setShowRealApp] = useState(() => checkIsWeb());

  const onDone = () => {
    setShowRealApp(true);
  };

  const onSkip = () => {
    setShowRealApp(true);
  };


  const RenderSliderItem = ({ item }: { item: any }) => {
    return (
      <View
        style={{
          flex: 1,
          backgroundColor: item.backgroundColor,
          alignItems: 'center',
          justifyContent: 'space-around',
          paddingBottom: 100,
        }}>
        <Text>{item.title}</Text>
        <Image source={item.image} />
        <Text>{item.text}</Text>
      </View>
    );
  };


  if (!isLoadingComplete) {
    return null;
  }
  return (
    <>
      {showRealApp ? (
        <SafeAreaProvider>
          <StatusBar />
          <NavigationContainer
            linking={LinkingConfiguration}>
            <Stack.Navigator>
              <Stack.Screen options={{ headerShown: false }} name="Login" component={LoginScreen}></Stack.Screen>
              <Stack.Screen options={{ headerShown: false }} name="Root" component={RootScreen}></Stack.Screen>
            </Stack.Navigator>
          </NavigationContainer>

        </SafeAreaProvider>
      ) : (
        <AppIntroSlider
          data={slides}
          renderItem={RenderSliderItem}
          onDone={onDone}
          showSkipButton={true}
          onSkip={onSkip}
        />
      )}
    </>
  );

}

const slides = [
  {
    key: 1,
    title: 'Title 1',
    text: 'Description.\nSay something cool',
    image: require('./assets/intro_screen/3.png'),
    backgroundColor: '#59b2ab',
  },
  {
    key: 2,
    title: 'Title 2',
    text: 'Other cool stuff',
    image: require('./assets/intro_screen/3.png'),
    backgroundColor: '#febe29',
  },
  {
    key: 3,
    title: 'Rocket guy',
    text: 'I\'m already out of descriptions\n\nLorem ipsum bla bla bla',
    image: require('./assets/intro_screen/3.png'),
    backgroundColor: '#22bcb5',
  }
];

const styles = StyleSheet.create({
  buttonCircle: {
    width: 40,
    height: 40,
    backgroundColor: 'rgba(0, 0, 0, .2)',
    borderRadius: 20,
    justifyContent: 'center',
    alignItems: 'center',
  }
});
