import { StyleSheet, FlatList, ActivityIndicator, View } from 'react-native';
import { auth } from "../firebase";
import EditScreenInfo from '../components/EditScreenInfo';
import { RootTabScreenProps } from '../types';
import { Button, ButtonGroup, withTheme, Text, Input, Icon, SearchBar, Image } from 'react-native-elements';
import React, { useState } from 'react';
import { PetCard } from '../components/PetCard';

export default function TabOneScreen({ navigation }: RootTabScreenProps<'TabOne'>) {
  const [search, setSearch] = useState("");

  const handleSignOut = () => {
    auth
      .signOut()
      .then(() => {
        return navigation.replace('Login');
      })
      .catch((error: { message: any; }) => alert(error.message))
  }
  const updateSearch = (search: React.SetStateAction<string>) => {
    setSearch(search);
  };

  return (
    <View style={styles.container}>
      <View style={styles.headContainer}>
        <Text style={styles.header}>Good Morning</Text>
        <Text style={styles.headerDesc}>Are you ready to find a new friend</Text>
      </View>
      <View style={styles.SearchBarContainer}>
        <SearchBar
          placeholder="Search for pets"
          onChangeText={() => updateSearch}
          value={search}
          platform="default"
          round={true}
          lightTheme={true}
          containerStyle={styles.containerStyle}
          inputContainerStyle={styles.inputContainerStyle}
        />
      </View>
      <View>
        <FlatList
          data={[...new Array(5)].map((_, i) => i.toString())}
          style={styles.list}
          numColumns={5}
          keyExtractor={(e) => e}
          renderItem={({ item }) => (
            <View style={styles.imageitemContainer}>
              <Image
                source={{ uri: BASE_URI + item }}
                containerStyle={styles.imageitem}
                PlaceholderContent={<ActivityIndicator />}
              />
              <Text style={{ fontWeight: 'bold' }}>PHOTO</Text>
            </View>

          )}
        />
      </View>
      <Text style={styles.recent}>Recent</Text>
      <FlatList
        data={DATA}
        renderItem={PetCard}
        keyExtractor={item => item.id}
      />

      {/* <View style={styles.MainContainer}>
        <Text style={styles.title}>Tab One</Text>
        <Text style={styles.title}>ddd</Text>
        <View style={styles.separator} lightColor="#eee" darkColor="rgba(255,255,255,0.1)" />
        <Button title="Logout" onPress={handleSignOut}></Button>
      </View> */}

    </View>
  );
}

const BASE_URI = 'https://source.unsplash.com/random?sig=';
const DATA = [
  {
    id: 'bd7acbea-c1b1-46c2-aed5-3ad53abb28ba',
    title: 'First Item',
    imagePath: 'petImage1'
  },
  {
    id: '3ac68afc-c605-48d3-a4f8-fbd91aa97f63',
    title: 'Second Item',
    imagePath: 'petImage2'
  },
  {
    id: '58694a0f-3da1-471f-bd96-145571e29d72',
    title: 'Third Item',
    imagePath: 'petImage3'
  },
];
const styles = StyleSheet.create({
  container: {
    flex: 1,
    padding: 30,
    backgroundColor: 'white'
  },
  inputContainerStyle: {
    backgroundColor: 'white', borderColor: '#aaaaaa', borderTopWidth: 1, borderBottomWidth: 1, borderLeftWidth: 1, borderRightWidth: 1
  },
  containerStyle: { backgroundColor: 'white', borderTopWidth: 0, borderBottomWidth: 0, padding: 0 },
  MainContainer: {
    flex: 1,
    alignItems: 'center',
    justifyContent: 'center',
  },
  headContainer: {
    overflow: 'hidden',
    justifyContent: 'space-between',
    paddingTop: 15,
    paddingBottom: 15
  },
  SearchBarContainer: {
    paddingBottom: 15
  },
  item: {
    backgroundColor: '#f9c2ff',
    padding: 20,
    marginVertical: 8,
  },
  title: {

  },
  header: {
    fontSize: 30,
    fontWeight: 'bold',
    color: '#298e96'
  },
  headerDesc: {
    fontSize: 15,
    color: '#aaaaaa'
  },
  separator: {
    marginVertical: 30,
    height: 1,
    width: '80%',
  },
  list: {
    height: 'auto',
    backgroundColor: 'white',
  },
  imageitem: {
    aspectRatio: 1,
    width: '100%',
    flex: 1,
    borderRadius: 25
  },
  imageitemContainer: {
    width: '100%',
    flex: 1,
    margin: 5,
    alignItems: 'center',
    paddingBottom: 15,
  },
  recent: {
    fontWeight: 'bold',
    fontSize: 20
  }
});
