import React, { useEffect, useState, useContext } from 'react';
import { StyleSheet, View, Image, TouchableOpacity, ScrollView, Platform } from 'react-native';
import { Button, ButtonGroup, withTheme, Text, Input, Divider } from 'react-native-elements';
import * as ImagePicker from 'expo-image-picker';
import * as FileSystem from 'expo-file-system';

import { storage, db, auth } from "../firebase";
import { ref as storage_ref, getDownloadURL, uploadBytes } from "firebase/storage";
import { collection, query, where, onSnapshot, getDocs, addDoc } from "firebase/firestore";

import { SettingStackProps, petPostDataProps } from '../types';
import Dropdown from '../components/dropdown'

export default function PostDetailScreen({ route, navigation }: { route: any, navigation: any }) {
  const { petItem } = route.params;

  return (
    <>
      <Pressable
        onPress={() => navigation.navigate('Home')}
        style={({ pressed }) => ({
          opacity: pressed ? 0.5 : 1,
          marginLeft: 20
        })}>
        <FontAwesome
          name="arrow-left"
          size={25}
          color="black"
          style={{ marginRight: 15 }}
        />
      </Pressable>
      <View style={styles.container}>
        {petItem.imagepath != "" && <Image style={styles.imageitem}
          source={{ uri: petItem.imagepath }}
        />}
        <Text>PostDetailScreen {petItem.id}</Text>
      </View >
    </>
  );
}

const styles = StyleSheet.create({
  container: {
    flex: 1,
    alignItems: 'flex-start',
    justifyContent: 'flex-start',
    padding: 20,
    backgroundColor: 'white',
    height: "100%"
  },
  imageitem: {
    aspectRatio: 0.95,
    width: '100%',
  },

});
