import { StyleSheet, View, Image, TouchableOpacity } from 'react-native';
import { SettingStackProps, petPostDataProps } from '../types';
import { Button, ButtonGroup, withTheme, Text, Input } from 'react-native-elements';
import { storage, db } from "../firebase";
import React, { useEffect, useState } from 'react';
import { ref, getDownloadURL } from "firebase/storage";
import { collection, query, where, onSnapshot, getDocs } from "firebase/firestore";
import * as ImagePicker from 'expo-image-picker';
import { Picker } from '@react-native-community/picker';
import { PetCard } from '../components/PetCard';

export default function CreatePostScreen() {
  const [imagepath, setImage] = useState("");
  const [petGender, setGenderValue] = useState<number | string>("Male");
  const [petStage, setStageValue] = useState<number | string>("Kid");
  const [petName, setPetName] = useState('');
  const [petAge, setPetAge] = useState<number | string>('');
  const [petDescription, setPetDescription] = useState('');

  const pickImage = async () => {
    // No permissions request is necessary for launching the image library
    let result = await ImagePicker.launchImageLibraryAsync({
      mediaTypes: ImagePicker.MediaTypeOptions.All,
      allowsEditing: true,
      aspect: [4, 3],
      quality: 1,
    });
    if (!result.cancelled) {
      setImage(result.uri);
    }
  };

  return (
    <>
      <View style={styles.container}>

        <Input style={styles.input} placeholder="Charlie" label='Pet Name:' onChangeText={text => setPetName(text)} />
        <Input style={styles.input} placeholder="0-100" label='Pet Age:' onChangeText={text => setPetAge(text)} />
        <View style={styles.pickerContainer}>
          <Text style={styles.pickerLabel} >Pet Stages:</Text>
          <Picker
            selectedValue={petGender}
            style={{ height: 50, width: 100, borderColor: 'white', fontSize: 16, color: 'rgb(36, 36, 36)' }}
            onValueChange={(itemValue, itemIndex) => {
              setStageValue(itemValue)
            }}>
            <Picker.Item label="Baby" value="baby" />
            <Picker.Item label="Kid" value="kid" />
            <Picker.Item label="Adult" value="adult" />
          </Picker>
        </View>
        <Input style={styles.input} placeholder="Descript your pet here" label='Pet Description:' onChangeText={text => setPetDescription(text)} />
        <View style={styles.pickerContainer}>
          <Text style={styles.pickerLabel} >Pet Gender:</Text>
          <Picker
            selectedValue={petGender}
            style={{ height: 50, width: 100, borderColor: 'white', fontSize: 16, color: 'rgb(36, 36, 36)' }}
            onValueChange={(itemValue, itemIndex) => {
              setGenderValue(itemValue)
            }}>
            <Picker.Item label="Male" value="male" />
            <Picker.Item label="Female" value="female" />
          </Picker>
        </View>
        <View style={styles.imagePickerContainer}>
          {imagepath != "" ?
            <TouchableOpacity onPress={pickImage}>
              <Image style={styles.imageitem}
                source={{ uri: imagepath }}
              />
            </TouchableOpacity>
            :
            <Button title="Pick an image from camera roll" onPress={pickImage} style={styles.imageitem} />
          }
          {imagepath != "" &&
            <Button title="Pick another image from camera roll" onPress={pickImage} style={styles.imageitem} />
          }
        </View>

      </View>

    </>
    //imagepath={imagepath} petname={petName} petgender={petGender} petstages={petStage} desc={petDescription} 
  );
}

const styles = StyleSheet.create({
  container: {
    flex: 1,
    alignItems: 'flex-start',
    justifyContent: 'flex-start',
    padding: 20,
    backgroundColor: 'white'
  },
  title: {
    fontSize: 20,
    fontWeight: 'bold',
  },
  separator: {
    marginVertical: 30,
    height: 1,
    width: '80%',
  },
  input: {
    fontSize: 12
  },
  pickerContainer: {
    paddingHorizontal: 10,

  },
  pickerLabel: {
    fontSize: 16,
    fontWeight: 'bold',
    color: 'rgb(134, 147, 158)'
  },
  CardContainer: {
    width: '100%',
    flex: 1,
    flexDirection: 'row',
    flexWrap: 'wrap',
    alignItems: 'flex-start', // if you want to fill rows left to right
    borderRadius: 25,
    padding: 10,
    borderWidth: 1,
    borderColor: '#dddddd',
    marginVertical: 15,
  },
  imageitem: {
    aspectRatio: 0.95,
    width: 200,
    borderRadius: 25,
  },
  contentContainer: {
    width: '50%',
    height: '100%',
    paddingLeft: 10,
    flex: 1,
  },
  petname: {
    fontWeight: 'bold',
    fontSize: 20
  },
  petgender: { fontSize: 10, marginBottom: 12, fontWeight: 'bold', color: '#888888' },
  desc: { fontSize: 10, color: '#999999' },
  askbutton: {
    backgroundColor: '#0b9298',
    borderRadius: 5,
    paddingHorizontal: 15
  },
  askbuttonContainer: {
    position: 'absolute',
    left: 10,
    bottom: 0,
  },
  imagePickerContainer: {
    flexDirection: 'row'
  }

});
