import { StyleSheet, View, Image } from 'react-native';
import { SettingStackProps, petPostDataProps } from '../types';
import { Button, ButtonGroup, withTheme, Text, Input } from 'react-native-elements';
import { storage, db } from "../firebase";
import React, { useEffect, useState } from 'react';
import { ref, getDownloadURL } from "firebase/storage";
import { collection, query, where, onSnapshot, getDocs } from "firebase/firestore";
import * as ImagePicker from 'expo-image-picker';
import { Picker } from '@react-native-community/picker';


export default function CreatePostScreen() {
  const [image, setImage] = useState("");
  const [open, setOpen] = useState(false);
  const [genderValue, setGenderValue] = useState<number | string>("Male");
  const pickImage = async () => {
    // No permissions request is necessary for launching the image library
    let result = await ImagePicker.launchImageLibraryAsync({
      mediaTypes: ImagePicker.MediaTypeOptions.All,
      allowsEditing: true,
      aspect: [4, 3],
      quality: 1,
    });
    if (!result.cancelled) {
      setImage(result.uri);
    }
  };

  return (
    <View style={styles.container}>

      <Input style={styles.input} label='Pet Name:' />
      <Input style={styles.input} label='Pet Age:' />
      <Input style={styles.input} label='Pet Description:' />
      <View style={styles.pickerContainer}>
        <Text style={styles.pickerLabel} >Pet Gender:</Text>
        <Picker
          selectedValue={genderValue}
          style={{ height: 50, width: 100, borderColor: 'white', fontSize: 16 }}
          onValueChange={(itemValue, itemIndex) => {
            setGenderValue(itemValue)
          }}>
          <Picker.Item label="Male" value="male" />
          <Picker.Item label="Female" value="female" />
        </Picker>

      </View>

      <View style={{ flex: 1, alignItems: 'center', justifyContent: 'center' }}>
        <Button title="Pick an image from camera roll" onPress={pickImage} />
        {image != "" && <Image source={{ uri: image }} style={{ width: 200, height: 200 }} />}
      </View>
    </View>

  );
}

const styles = StyleSheet.create({
  container: {
    flex: 1,
    alignItems: 'flex-start',
    justifyContent: 'flex-start',
    padding: 20,
    backgroundColor: 'white'
  },
  title: {
    fontSize: 20,
    fontWeight: 'bold',
  },
  separator: {
    marginVertical: 30,
    height: 1,
    width: '80%',
  },
  input: {
    fontSize: 12
  },
  pickerContainer: {
    paddingHorizontal: 10,

  },
  pickerLabel: {
    fontSize: 16,
    fontWeight: 'bold',
    color: 'rgb(134, 147, 158)'
  },
  CardContainer: {
    flex: 1,
    flexDirection: 'row',
    flexWrap: 'wrap',
    alignItems: 'flex-start', // if you want to fill rows left to right
    borderRadius: 25,
    padding: 10,
    borderWidth: 1,
    borderColor: '#dddddd',
    marginVertical: 15,
  },
  imageitem: {
    aspectRatio: 0.95,
    width: 100,
    borderRadius: 25,
  },
});
