import { PetCard } from '../components/PetCard';
import { StyleSheet, FlatList, ActivityIndicator, View } from 'react-native';
import { ref, getDownloadURL } from "firebase/storage";
import { collection, query, where, onSnapshot, getDocs } from "firebase/firestore";
import React, { useEffect, useState } from 'react';
import { SettingStackProps, petPostDataProps } from '../types';
import { storage, db } from "../firebase";

export function PetCardFlatList() {
    const [petPostDataArray, setpetPostDataArray] = useState<petPostDataProps[]>([]);

    const getData = async () => {
        const querySnapshot = await getDocs(collection(db, "PetPost"));
        querySnapshot.forEach((doc) => {
            console.log(doc.id, " => ", doc.data());
            getDownloadURL(ref(storage, doc.data()['imagepath']))
                .then((url) => {
                    const newjson = {
                        id: doc.id,
                        imagepath: url,
                        petname: doc.data()['petname'],
                        petgender: doc.data()['petgender'],
                        petstages: doc.data()['petstages'],
                        desc: doc.data()['desc']
                    }
                    setpetPostDataArray(oldArray => [...oldArray, newjson]);

                })
                .catch((error) => {
                    // Handle any errors
                });

        });

    }

    useEffect(() => {
        getData()
    }, []);


    return (
        <FlatList
            showsVerticalScrollIndicator={false}
            showsHorizontalScrollIndicator={false}
            data={petPostDataArray}
            renderItem={PetCard}
            keyExtractor={item => item.id}
        />
    );
}

const DATA = [
    {
        id: 'bd7acbea-c1b1-46c2-aed5-3ad53abb28ba',
        title: 'First Item',
        imagepath: 'petImage1',
        petname: 'Elizabeth',
        petgender: 'Female',
        petstages: 'adult',
        desc: 'Lorem ipsum dolor molestias nemo sit amet. Sed odit commodi est commodi dicta sit doloribus veniam sed commodi fuga eum voluptate Quis'
    },
    {
        id: '3ac68afc-c605-48d3-a4f8-fbd91aa97f63',
        title: 'Second Item',
        imagepath: 'petImage2',
        petname: 'Lucas',
        petgender: 'Female',
        petstages: 'adult',
        desc: 'Aut molestias minus eos quis minus nihil nemo eos nobis libero cum dolorum aliquam et laboriosam sunt quo porro tempora'
    },
    {
        id: '58694a0f-3da1-471f-bd96-145571e29d72',
        title: 'Third Item',
        imagepath: 'petImage3',
        petname: 'Cat',
        petgender: 'Female',
        petstages: 'adult',
        desc: 'Aut molestias minus eos quis minus nihil nemo eos nobis libero cum dolorum aliquam et laboriosam sunt quo porro tempora'
    },
];
