import { StyleSheet, FlatList, ActivityIndicator, View } from 'react-native';
import { auth } from "../firebase";
import EditScreenInfo from '../components/EditScreenInfo';
import { RootTabScreenProps } from '../types';
import { Button, ButtonGroup, withTheme, Text, Input, Icon, SearchBar, Image } from 'react-native-elements';
import React, { useState } from 'react';
import { PetCard } from '../components/PetCard';
import { LinearGradient } from 'expo-linear-gradient';

export default function HomeScreen({ navigation }: RootTabScreenProps<'Home'>) {
  const [search, setSearch] = useState("");

  const handleSignOut = () => {
    auth
      .signOut()
      .then(() => {
        return navigation.replace('Login');
      })
      .catch((error: { message: any; }) => alert(error.message))
  }
  const updateSearch = (search: React.SetStateAction<string>) => {
    setSearch(search);
  };

  return (<>
    <View style={styles.container}>
      <View style={styles.headContainer}>
        <Text style={styles.header}>Good Morning</Text>
        <Text style={styles.headerDesc}>Are you ready to find a new friend</Text>
      </View>
      <View style={styles.SearchBarContainer}>
        <SearchBar
          placeholder="Search for pets"
          onChangeText={() => updateSearch}
          value={search}
          platform="default"
          round={true}
          lightTheme={true}
          containerStyle={styles.containerStyle}
          inputContainerStyle={styles.inputContainerStyle}
        />
      </View>
      <View>

        <FlatList
          data={petCatagoryData}
          numColumns={5}
          keyExtractor={item => item.id}
          renderItem={({ item }) => (
            <View style={styles.imageitemContainer}>
              <Image
                source={require('../assets/images/cat.png')}
              />
              <Text style={{ fontWeight: 'bold' }}>PHOTO</Text>
            </View>
          )}
        />
      </View>
      <Text style={styles.recent}>Recent</Text>
      <View style={{ flex: 1, backgroundColor: 'transparent', top: '0', width: '100%', height: '40px', zIndex: 10 }}>
        <LinearGradient
          colors={['rgba(255,255,255,1)', 'rgba(0,255,255,0)']}
          style={{ height: 40 }}
        />
      </View>
      <FlatList
        showsVerticalScrollIndicator={false}
        showsHorizontalScrollIndicator={false}
        data={DATA}
        renderItem={PetCard}
        keyExtractor={item => item.id}
      />
    </View>
    <View style={{ flex: 1, backgroundColor: 'transparent', position: 'absolute', bottom: '0', width: '100%', height: '40px', }}>
      <LinearGradient
        colors={['rgba(255,255,255,0)', 'rgba(255,255,255,1)']}
        style={{ height: 40 }}
      />
    </View>
  </>
  );
}

