import React from 'react';
import { Image, StyleSheet, View, TouchableHighlight } from 'react-native';
import { Dialog, Text } from 'react-native-elements';


export const PetSocialMediaCard = (petPostDataItem: any, showImageDialog: boolean, setShowImageDialog: any) => (
    <View style={styles.CardContainer}>
        {
            petPostDataItem.item.imagepath != "" &&
            <View>
                <TouchableHighlight
                    onPress={setShowImageDialog(true)}
                    onLongPress={setShowImageDialog(true)} underlayColor="white">

                    <Image style={styles.imageitem}
                        source={{ uri: petPostDataItem.item.imagepath }}
                    />
                </TouchableHighlight >


                <Dialog isVisible={showImageDialog}>
                    <Image style={styles.imageitem}
                        source={{ uri: petPostDataItem.item.imagepath }}
                    />
                    <Text>{petPostDataItem.item.caption}</Text>
                    <Text>{timestampToDate(petPostDataItem.item.createdate.seconds)}</Text>
                </Dialog>
            </View>

        }
    </View >
);

const styles = StyleSheet.create({
    CardContainer: {
        width: '33%',
        borderColor: '#fefefe',
        borderWidth: 1
    },
    imageitem: {
        aspectRatio: 1,
    },
});


function timestampToDate(t: number) {
    var date = new Date(t * 1000);

    var hours = date.getHours();

    var minutes = "0" + date.getMinutes();

    var seconds = "0" + date.getSeconds();

    var formattedTime = hours + ':' + minutes.substr(-2) + ':' + seconds.substr(-2);

    return formattedTime;

}
