import * as WebBrowser from 'expo-web-browser';
import { ReactChild, ReactFragment, ReactPortal } from 'react';
import { StyleSheet, FlatList, ActivityIndicator, View, Image, Pressable } from 'react-native';

import { ButtonGroup, withTheme, Text, Input, Icon, SearchBar, Button } from 'react-native-elements';

const assetsObject = require('../assets/asset.tsx')

export const PetCard = ({ item }: any) => (

    <View style={styles.CardContainer}>
        <Image style={styles.imageitem}
            source={assetsObject[item.imagepath]}
        />
        <View style={styles.contentContainer}>
            <Text style={styles.petname}>{item.petname}</Text>
            <Text style={styles.petgender}>{item.petgender}  -  {item.petstages} </Text>
            <Text style={styles.desc}>{item.desc} </Text>
            <Button buttonStyle={styles.askbutton}
                containerStyle={styles.askbuttonContainer}
                titleStyle={{ fontWeight: 'bold', fontSize: 11 }}
                title="Ask about me">
            </Button>
        </View>


    </View>
);


const styles = StyleSheet.create({
    CardContainer: {
        flex: 1,
        flexDirection: 'row',
        flexWrap: 'wrap',
        alignItems: 'flex-start', // if you want to fill rows left to right
        borderRadius: 25,
        padding: 10,
        borderWidth: 1,
        borderColor: '#dddddd',
        marginVertical: 15,
        height: 200
    },
    contentContainer: {
        width: '50%',
        height: '100%',
        paddingLeft: 10,
        flex: 1,

    },
    petname: {
        fontWeight: 'bold',
        fontSize: 22
    },
    petgender: { fontSize: 11, marginBottom: 12, fontWeight: 'bold', color: '#888888' },
    desc: { fontSize: 11, color: '#999999' },
    imageitem: {
        aspectRatio: 0.8,
        width: '50%',
        borderRadius: 25,
    },
    askbutton: {
        backgroundColor: '#0b9298',
        borderRadius: 5,
        paddingHorizontal: 15
    },
    askbuttonContainer: {
        position: 'absolute',
        left: 10,
        bottom: 10,
    }

});
