import React, { useContext } from 'react';
import { StyleSheet, View, Pressable } from 'react-native';
import { createBottomTabNavigator } from '@react-navigation/bottom-tabs';
import { RootStackParamList, RootTabParamList, RootTabScreenProps } from '../types';
import HomeScreen from './HomeScreen';
import TabTwoScreen from './TabTwoScreen';
import ProfileScreen from './SettingScreen';
import { Feather } from '@expo/vector-icons';

export default function RootScreen({ navigation }: { navigation: any }) {
  const BottomTab = createBottomTabNavigator<RootTabParamList>();
  return (
    <BottomTab.Navigator
      initialRouteName="Home"
      screenOptions={{
        tabBarActiveTintColor: '#0b9298',
        tabBarStyle: { borderTopWidth: 0, backgroundColor: 'white', height: '8%' }
      }}>
      <BottomTab.Screen
        name="Home"
        component={HomeScreen}
        options={{
          headerShown: false,
          title: '',
          tabBarIcon: ({ color }) => <Feather name="home" color={color} size={25} />,
        }}
      />
      <BottomTab.Screen
        name="Inbox"
        component={TabTwoScreen}
        options={{
          title: '',
          tabBarIcon: ({ color }) => <Feather name="inbox" color={color} size={25} />,
        }}
      />
      <BottomTab.Screen
        name="Favourite"
        component={TabTwoScreen}
        options={{
          title: '',
          tabBarIcon: ({ color }) => <Feather name="heart" color={color} size={25} />,
        }}
      />
      <BottomTab.Screen
        name="Settings"
        component={ProfileScreen}
        options={{
          title: '',
          tabBarIcon: ({ color }) => <Feather name="user" color={color} size={25} />,
        }}
      />
    </BottomTab.Navigator>
  );
}
