import React, { useEffect, useState, useContext } from 'react';
import { StyleSheet, View, Image, TouchableOpacity, ScrollView, Platform } from 'react-native';
import { Button, ButtonGroup, withTheme, Text, Input, Divider, Avatar } from 'react-native-elements';
import * as ImagePicker from 'expo-image-picker';
import * as FileSystem from 'expo-file-system';
import { FontAwesome } from '@expo/vector-icons';
import { storage, db, auth } from "../firebase";
import { ref as storage_ref, getDownloadURL, uploadBytes } from "firebase/storage";
import { collection, query, where, onSnapshot, doc, updateDoc } from "firebase/firestore";

import { SettingStackProps } from '../types';
import Dropdown from '../components/dropdown'
const assetsObject = require('../assets/asset.tsx')

export default function UserSettingScreen({ navigation }: SettingStackProps<'Setting'>) {
  const [imagepath, setImage] = useState<string | undefined>('');
  const [downloadURL, setDownloadURL] = useState("");
  const [userId, setUserId] = useState<string | undefined>('');
  const [UserGender, setGenderValue] = useState<string | undefined>("Male");
  const [UserName, setUserName] = useState<string | undefined>('');
  const [UserAge, setUserAge] = useState<string | undefined>('');
  const [UserDescription, setUserDescription] = useState<string | undefined>('');
  const [uploading, setUploading] = useState(false);
  const [submitted, setSubmitted] = useState(false);
  const [selectedButtonIndex, setSelectedButtonIndex] = useState(2);
  const [submitButtomText, setSubmitButtomText] = useState('Submit');
  const [isLoading, setIsLoading] = useState(true);


  const getData = async () => {
    var userId = auth.currentUser?.uid;
    console.log("Get data: userId:", userId);
    if (!userId) return false;
    const unsub = onSnapshot(doc(db, "Users", userId), (doc) => {
      if (doc == undefined) return false;
      if (!doc.data()) return false;

      if (doc.data()?.desc) setUserDescription(doc.data()?.desc);
      if (doc.data()?.username) setUserName(doc.data()?.username);
      if (doc.data()?.usergender) setGenderValue(doc.data()?.usergender);
      if (doc.data()?.userAge) setUserAge(doc.data()?.userAge);
      if (doc.data()?.imagepath) setImage(doc.data()?.imagepath);

    });

  }

  useEffect(() => {
    console.log("auth.currentUser?.uid", auth.currentUser?.uid);
    console.log("imagepath", imagepath)
    setUserId(auth.currentUser?.uid);
    getData()
    setIsLoading(false)
  }, []);

  const pickImageByLibrary = async () => {
    let result = await ImagePicker.launchImageLibraryAsync({
      mediaTypes: ImagePicker.MediaTypeOptions.All,
      allowsEditing: true,
      aspect: [4, 4],
      maxWidth: 500,
      maxHeight: 500,
      quality: 0.5,
    });
    if (!result.cancelled) {
      setImage(result.uri);
    }

  };

  const pickImageByCamera = async () => {
    let result = await ImagePicker.launchCameraAsync({
      mediaTypes: ImagePicker.MediaTypeOptions.All,
      allowsEditing: true,
      aspect: [4, 4],
      quality: 0.5,
    });
    if (!result.cancelled) {
      setImage(result.uri);
    }
    console.log(Platform.OS)
  };

  const uploadPost = async () => {
    setUploading(true)

    console.log('user uploading image ', userId);

    //Set Image
    var snapshotDownloadURL = null
    if (imagepath) {
      const imagename = Date.now() + '-' + getRandomString(5) + '-' + userId
      const storageRef = storage_ref(storage, 'userIcon/' + imagename + '.png');

      const blob: Blob = await new Promise((resolve, reject) => {
        const xhr = new XMLHttpRequest();
        xhr.onload = function () {
          resolve(xhr.response);
        };
        xhr.onerror = function () {
          reject(new TypeError("Network request failed"));
        };
        xhr.responseType = "blob";
        xhr.open("GET", imagepath, true);
        xhr.send(null);
      });
      const snapshot = await uploadBytes(storageRef, blob);
      snapshotDownloadURL = await getDownloadURL(snapshot.ref);
      setDownloadURL(snapshotDownloadURL);
      console.log('File available at', snapshotDownloadURL);
    } else {
      console.log('user no image ', userId);
    }

    //Set User Doc
    try {
      if (userId) {
        const UserRef = doc(db, 'Users', userId);
        await updateDoc(UserRef, {
          imagepath: snapshotDownloadURL,
          username: UserName,
          usergender: UserGender,
          desc: UserDescription,
          lastupdateDate: new Date(),
          userAge: UserAge
        });
        console.log("Document written with ID: ", UserRef.id);
      } else {
        console.error("userId not found:  ", auth.currentUser);
      }
    } catch (e) {
      console.error("Error adding document:  ", e);
    }

    setSubmitted(true)
    setUploading(false)
    setSubmitButtomText("Submitted")
  }

  return (
    <>
      <View style={styles.container}>
        <ScrollView showsVerticalScrollIndicator={false}
          showsHorizontalScrollIndicator={false} style={{ height: "100%", width: "100%", paddingTop: 15 }}>
          <View style={styles.imagePickerContainer}>
            <View style={styles.imageitem}>
              {(imagepath != "" && imagepath != undefined) ?
                <TouchableOpacity onPress={pickImageByLibrary}>
                  <Avatar
                    size={100}
                    rounded
                    source={{ uri: imagepath }}
                  />
                </TouchableOpacity> :
                <TouchableOpacity onPress={pickImageByLibrary}>
                  <Avatar
                    size={100}
                    rounded
                    title="U"
                    containerStyle={{ backgroundColor: '#aaaaaa' }}

                  />
                </TouchableOpacity>
              }
              <View style={styles.iconContainer}>
                <FontAwesome
                  name="camera"
                  size={25}
                  rounded
                  color="black"
                  iconStyle={{ backgroundColor: '#eb1561' }}
                  containerStyle={{ backgroundColor: '#eb1561' }}
                  avatarStyle={{ backgroundColor: '#eb1561' }}
                  overlayContainerStyle={{ backgroundColor: '#eb1561' }}
                  placeholderStyle={{ backgroundColor: '#eb1561' }}
                  onPress={() => { pickImageByLibrary }}
                />
              </View>
            </View>

          </View>

          <Input style={styles.input} placeholder="Charlie" label='User Name:'
            onChangeText={text => setUserName(text)} containerStyle={{ marginBottom: 20 }} value={UserName} />
          <Input style={styles.input} placeholder="0-100" label='User Age:'
            onChangeText={text => setUserAge(text)} containerStyle={{ marginBottom: 20 }} value={UserAge} />
          <Input style={styles.input} placeholder="Descript your User here" label='User Description:'
            onChangeText={text => setUserDescription(text)} containerStyle={{ marginBottom: 20 }} value={UserDescription}
            numberOfLines={4}
          />
          <View style={styles.pickerContainer}>
            <Text style={styles.pickerLabel} >User Gender:</Text>
            <Dropdown data={gender_drop_down_data} initvalue={UserGender} />
          </View>
          <Button title={submitButtomText} onPress={uploadPost}
            loading={uploading}
            disabled={submitted}
            buttonStyle={{
              backgroundColor: 'rgba(111, 202, 186, 1)',
              borderRadius: 5,
              margin: 10, marginTop: 30
            }}
            titleStyle={{ fontSize: 12 }} />
        </ScrollView>
      </View >
    </>
  );
}

const styles = StyleSheet.create({
  container: {
    flex: 1,
    alignItems: 'flex-start',
    justifyContent: 'flex-start',
    padding: 20,
    backgroundColor: 'white',
    height: "100%"
  },
  title: {
    fontSize: 20,
    fontWeight: 'bold',
  },
  separator: {
    marginVertical: 30,
    height: 1,
    width: '80%',
  },
  input: {
    fontSize: 12
  },
  pickerContainer: {
    paddingHorizontal: 10,
    marginBottom: 30

  },
  pickerLabel: {
    fontSize: 16,
    fontWeight: 'bold',
    color: 'rgb(134, 147, 158)'
  },
  CardContainer: {
    width: '100%',
    flex: 1,
    flexDirection: 'row',
    flexWrap: 'wrap',
    alignItems: 'flex-start', // if you want to fill rows left to right
    borderRadius: 25,
    padding: 10,
    borderWidth: 1,
    borderColor: '#dddddd',
    marginVertical: 15,
  },
  imageitem: {
    aspectRatio: 0.95,
    borderRadius: 25,
    fontSize: 12,

  },
  contentContainer: {
    width: '50%',
    height: '100%',
    paddingLeft: 10,
    flex: 1,
  },
  Username: {
    fontWeight: 'bold',
    fontSize: 20
  },
  Usergender: {
    fontSize: 10,
    marginBottom: 12,
    fontWeight: 'bold',
    color: '#888888'
  },
  desc: {
    fontSize: 10,
    color: '#999999'
  },
  askbutton: {
    backgroundColor: '#0b9298',
    borderRadius: 5,
    paddingHorizontal: 15
  },
  askbuttonContainer: {
    position: 'absolute',
    left: 10,
    bottom: 0,
  },
  imagePickerContainer: {
    flexDirection: 'column',
    paddingHorizontal: 10,
    alignItems: 'center',
    alignSelf: 'center',
    paddingBottom: 20
  },
  paddingView: {
    paddingTop: 10
  },
  iconContainer: {
    position: 'absolute',
    zIndex: 1,
    right: 0,
    bottom: 0
  },
