import React, { useEffect, useState, useContext } from 'react';
import { StyleSheet, View, Image, TouchableOpacity, ScrollView, Platform, Pressable } from 'react-native';
import { Button, ButtonGroup, withTheme, Text, Input, Divider, Avatar, ListItem, Icon } from 'react-native-elements';
import * as ImagePicker from 'expo-image-picker';
import * as FileSystem from 'expo-file-system';
import { FontAwesome } from '@expo/vector-icons';
import { storage, db, auth } from "../firebase";
import { ref as storage_ref, getDownloadURL, uploadBytes } from "firebase/storage";
import { collection, query, where, onSnapshot, getDocs, addDoc, doc, setDoc, updateDoc } from "firebase/firestore";

import { SettingStackProps, petPostDataProps } from '../types';
import Dropdown from '../components/dropdown';
const assetsObject = require('../assets/asset.tsx')

export default function PetRequestDetailScreen({ route, navigation }: { route: any, navigation: any }) {
  const { petPostDataItem } = route.params;
  const [ownerName, setOwnerName] = useState<string | undefined>('');
  const [ownerImagepath, setOwnerImage] = useState("");
  const [requestUserList, setRequstUserList] = useState<any>([])
  const [selectedButtonIndex, setSelectedButtonIndex] = useState<any>([]);

  const getRequestDetail = async (requestID: string) => {
    console.log("Get data: ", requestID);
    if (!requestID) return false;

    const unsub = onSnapshot(doc(db, "Users", requestID), (doc) => {
      console.log("Current data: ", doc.data());
      if (doc == undefined) return false;
      if (!doc.data()) return false;

      const newjson = {
        userId: doc.id,
        userName: doc.data()?.username,
        userImage: doc.data()?.imagepath,
        userDesc: doc.data()?.desc,
        userEmail: doc.data()?.email,
        userAge: doc.data()?.userAge,
      }
      setRequstUserList((oldArray: any) => [...oldArray, newjson]);
    });
  }

  const setPetAdopter = async (swipeableIndex: number) => {
    console.log(requestUserList);
    console.log(requestUserList[swipeableIndex]);

    var requestUserID = requestUserList[swipeableIndex].userId
    try {
      if (!requestUserID || petPostDataItem.id) {
        console.error("requestUserID or petPostDataItem.id not found:  ", requestUserID);
        return false;
      }

      const petPostRef = doc(db, 'PetPost', petPostDataItem.id);
      await updateDoc(petPostRef, {
        adopterID: requestUserID
      });
      console.log("Document written with ID: ", petPostRef.id);

    } catch (e) {
      console.error("Error adding document:  ", e);
    }
  }

  const sendContactRequest = async () => {
    var userId = auth.currentUser?.uid;

    try {
      if (!userId) {
        console.error("userId not found:  ", auth.currentUser);
        return
      }

      var postRef = doc(db, "PetPost", petPostDataItem.id);
      var requestRef = doc(postRef, "contactRequest", userId);

      const unsub = await setDoc(requestRef, {
        status: 'New request',
        requestTime: new Date(),
      }, { merge: true });

      console.log("Document written with ID: ", petPostDataItem.id);

    } catch (e) {
      console.error("Error adding document:  ", e);
    }

  }


  useEffect(() => {
    console.log("petPostDataItem:", petPostDataItem.item.requestUsers)
    if (petPostDataItem.item.requestUsers) {
      petPostDataItem.item.requestUsers.forEach((requestID: string, index: any) => {
        getRequestDetail(requestID);
      })
    }
  }, []);

  const list = [
    {
      name: 'Amy Farha',
      avatar_url: 'https://s3.amazonaws.com/uifaces/faces/twitter/ladylexy/128.jpg',
      subtitle: 'Vice President'
    },
    {
      name: 'Chris Jackson',
      avatar_url: 'https://s3.amazonaws.com/uifaces/faces/twitter/adhamdannaway/128.jpg',
      subtitle: 'Vice Chairman'
    }
  ];

  return (
    <>
      <View style={styles.container}>
        <Text></Text>
        <View>
          {
            requestUserList.map((l: any, i: number) => (
              <ListItem.Swipeable key={i} bottomDivider
                leftContent={
                  <Button
                    title="Reject"
                    icon={{ name: 'close', color: 'white' }}
                    buttonStyle={{ height: '100%', backgroundColor: 'red' }}
                    onPress={() => {
                      console.log('Reject', i)
                    }
                    }
                  />
                }
                leftStyle={{ height: '100%', backgroundColor: 'red', justifyContent: 'center' }}
                rightContent={
                  <Button
                    title="Accpet"
                    icon={{ name: 'check', color: 'white' }}
                    buttonStyle={{ minHeight: '100%', backgroundColor: 'green' }}
                    onPress={() => {
                      console.log('Accept', i)
                      setPetAdopter(i)
                    }
                    }
                  />
                }
                rightStyle={{ height: '100%', backgroundColor: 'green', justifyContent: 'center' }}

              >
                <Avatar source={{ uri: l.userImage ? l.userImage : assetsObject["blankIcon"] }} rounded />
                <ListItem.Content>
                  <ListItem.Title>{l.userEmail} </ListItem.Title>
                  <ListItem.Subtitle>{l.userName}</ListItem.Subtitle>
                  <ListItem.Subtitle>{l.userDesc}</ListItem.Subtitle>
                </ListItem.Content>
              </ListItem.Swipeable>
            ))
          }
        </View>
      </View >
    </>
  );
}

const styles = StyleSheet.create({
  container: {
    // flex: 1,
    // alignItems: 'flex-start',
    // justifyContent: 'flex-start',
    backgroundColor: 'white',
    height: "100%"
  },
  iconContainer: {
    padding: 20,
    alignSelf: 'flex-start',
    zIndex: 1,
    position: 'absolute'
  },
  iconContainer2: {
    padding: 20,
    alignSelf: 'flex-end',
    zIndex: 1,
    position: 'absolute'
  },
  imageitem: {
    zIndex: 0,
    aspectRatio: 1.1,
    width: '100%',
  },
  contentContainer: {
    width: '100%',
    height: '100%',
    padding: 30,
    flex: 1,
    borderRadius: 25,
    backgroundColor: 'white',
    top: -30
  },
  petname: {
    fontWeight: 'bold',
    fontSize: 30
  },
  petgender: {
    fontSize: 10, marginBottom: 12, fontWeight: 'bold', color: '#888888'
  },
  desc: {
    fontSize: 10, color: '#999999'
  },
  contactbutton: {
    backgroundColor: '#0b9298',
    borderRadius: 5,
    paddingHorizontal: 15,
    marginRight: 30
  },
  contactbuttonContainer: {
    marginHorizontal: 30
  },
  footerContainer: {
    position: 'absolute',
    flexDirection: 'row',
    justifyContent: 'space-between',
    alignItems: 'center',
    bottom: 0,
    width: '100%',
    height: '10%',
  },
  ownerContainer: {
    marginLeft: 30,
    flexDirection: 'row',
    justifyContent: 'flex-start',
  },
  ownerDetailContainer: {
    marginLeft: 10,
    flexDirection: 'column',
    justifyContent: 'center',
    alignItems: 'flex-start'
  },


});
