import * as WebBrowser from 'expo-web-browser';
import { StyleSheet, FlatList, ActivityIndicator, View, Image } from 'react-native';

import { Button, ButtonGroup, withTheme, Text, Input, Icon, SearchBar } from 'react-native-elements';
import { TextProps } from './Themed';
const assets = require('../assets/asset')

const CardItem = ({ ...props }) => (
    <View style={styles.CardContainer}>
        <Image style={styles.imageitem}
            source={assets[props.imagePath]}
        />
        <Text style={styles.contentContainer}>{props.title} 2</Text>
    </View>
);

export const PetCard = ({ item }: any) => (
    <CardItem title={item.title} />
);


const styles = StyleSheet.create({
    CardContainer: {
        flex: 1,
        flexDirection: 'row',
        flexWrap: 'wrap',
        alignItems: 'flex-start', // if you want to fill rows left to right
        borderRadius: 25,
        padding: 10,
        borderWidth: 1,
        borderColor: '#aaaaaa',
        marginVertical: 20
    },
    contentContainer: {
        width: '60%',
        backgroundColor: '#aaaaaa'

    },
    title: {

    },
    imageitem: {
        aspectRatio: 1,
        width: '40%',
        flex: 1,
        borderRadius: 25,
    }

});
