import { StyleSheet, View, Pressable } from 'react-native';
import { auth } from "../firebase";
import { useEffect, useLayoutEffect } from 'react';
import { FontAwesome } from '@expo/vector-icons';
import { Button, ButtonGroup, withTheme, Text, Input } from 'react-native-elements';
import { MyFavParamList, MyFavStackProps } from '../types';
import { createNativeStackNavigator } from '@react-navigation/native-stack';
import SettingScreen from '../screens/SettingScreen';
import UserSettingScreen from '../screens/UserSettingScreen';

import MyFavScreen from '../screens/MyFavScreen';
import PetSocialMediaScreen from '../screens/PetSocialMediaScreen';

const Stack = createNativeStackNavigator<MyFavParamList>();

export default function SettingsScreen({ navigation }: MyFavStackProps<'MyFav'>) {
  useLayoutEffect(() => {
    navigation.getParent()?.setOptions({ headerShown: false });
    return () => navigation.getParent()?.setOptions(
      { headerShown: true, title: 'My Favourite', }
    );
  }, [navigation]);

  useEffect(() => {

  }, [navigation]);

  return (
    <Stack.Navigator>
      <Stack.Screen options={{ headerShown: false }} name="MyFav" component={MyFavScreen}
      ></Stack.Screen>
      <Stack.Screen name="PetSocialMedia" component={PetSocialMediaScreen}
        options={{
          title: 'Pet Social Media',
          headerLeft: () => (
            <Pressable
              onPress={() => navigation.navigate('MyFav')}
              style={({ pressed }) => ({
                opacity: pressed ? 0.5 : 1,
                marginLeft: 20
              })}>
              <FontAwesome
                name="arrow-left"
                size={25}
                color="black"
                style={{ marginRight: 15 }}
              />
            </Pressable>
          ),
        }}
      ></Stack.Screen>
    </Stack.Navigator>
  );
}

const styles = StyleSheet.create({
  container: {
    flex: 1,
    alignItems: 'center',
    justifyContent: 'center',
  },
  title: {
    fontSize: 20,
    fontWeight: 'bold',
  },
  separator: {
    marginVertical: 30,
    height: 1,
    width: '80%',
  },
});
