import { ReactChild, ReactFragment, ReactPortal } from 'react';
import { StyleSheet, FlatList, ActivityIndicator, View, Image, Pressable } from 'react-native';

import { ButtonGroup, withTheme, Text, Input, Icon, SearchBar, Button } from 'react-native-elements';

type imageOptions = {
    [key: string]: any
}
const assetsObject: imageOptions = {
    Cat: require('../assets/images/PetCatagory/dog.jpeg'),
    Dog: require('../assets/images/PetCatagory/cat.png'),
    Bird: require('../assets/images/PetCatagory/hamster.jpeg'),
    Hamster: require('../assets/images/PetCatagory/bird.jpg'),
    Rabbit: require('../assets/images/PetCatagory/rabbit.jpg'),
}

export const PetCatagory = ({ item }: any) => (
    <View style={styles.imageitemContainer}>
        <Image
            source={assetsObject[item.title]}
        />
        <Text style={{ fontWeight: 'bold' }}>{item.title}</Text>
    </View>
);


const styles = StyleSheet.create({
    CardContainer: {
        flex: 1,
        flexDirection: 'row',
        flexWrap: 'wrap',
        alignItems: 'flex-start', // if you want to fill rows left to right
        borderRadius: 25,
        padding: 10,
        borderWidth: 1,
        borderColor: '#dddddd',
        marginVertical: 15
    },
    contentContainer: {
        width: '50%',
        paddingLeft: 10,
        flex: 1,
        height: '100%'
    },
    petname: {
        fontWeight: 'bold',
        fontSize: 22
    },
    petgender: { fontSize: 11, marginBottom: 12, fontWeight: 'bold', color: '#888888' },
    desc: { fontSize: 11, color: '#999999' },
    imageitem: {
        aspectRatio: 0.75,
        width: '50%',
        borderRadius: 25,
    },
    askbutton: {
        backgroundColor: '#0b9298',
        borderRadius: 5,
        paddingHorizontal: 15
    },
    askbuttonContainer: {
        position: 'absolute',
        left: 10,
        bottom: 10,
    },
    imageitemContainer: {
        width: '100%',
        flex: 1,
        margin: 5,
        alignItems: 'center',
        paddingBottom: 15,
    },

});
