import { StatusBar } from 'expo-status-bar';
import React, { useContext } from 'react';
import { StyleSheet, View, Pressable } from 'react-native';
import { Button, ButtonGroup, withTheme, Text, Input, Icon } from 'react-native-elements';
import { createBottomTabNavigator } from '@react-navigation/bottom-tabs';
import { auth } from "../firebase";
import { RootStackParamList, RootTabParamList, RootTabScreenProps } from '../types';
import TabOneScreen from '../screens/TabOneScreen';
import TabTwoScreen from '../screens/TabTwoScreen';
import { FontAwesome } from '@expo/vector-icons';
import Colors from '../constants/Colors';
import { getHeaderTitle } from '@react-navigation/elements';

function MyHeader(title: string, style: any) {
  return (
    <View style={{ paddingTop: 20, paddingBottom: 20, backgroundColor: 'yellow' }}>
      <View style={{ flexDirection: 'row', alignItems: 'center', borderColor: 'red', borderWidth: 1 }}>
        <Text style={{ flex: 1, borderColor: 'red', borderWidth: 1 }}>Left with long sd asdf text</Text>
        <Text style={{ flex: 1, borderColor: 'red', borderWidth: 1, textAlign: 'center' }}>{title}</Text>
        <Text style={{ flex: 1, borderColor: 'red', borderWidth: 1, textAlign: 'right' }}>Right</Text>
      </View>
    </View>
  )
}

export default function HomeScreen({ navigation }: { navigation: any }) {
  const handleSignOut = () => {

    auth
      .signOut()
      .then(() => {
        navigation.replace('Login')
      })
      .catch((error: { message: any; }) => alert(error.message))
  }
  const BottomTab = createBottomTabNavigator<RootTabParamList>();
  return (
    <BottomTab.Navigator
      initialRouteName="TabOne"
      screenOptions={{
        tabBarActiveTintColor: '#000396',
        header: ({ navigation, route, options }) => {
          const title = getHeaderTitle(options, route.name);

          return <MyHeader title={title} style={styles.headerStyle} />;
        }
      }}>
      <BottomTab.Screen
        name="TabOne"
        component={TabOneScreen}
        options={({ navigation }: RootTabScreenProps<'TabOne'>) => ({
          title: 'Good Morning',
          tabBarStyle: { position: 'absolute', color: '#0a9396' },
          tabBarIcon: ({ color }) => <TabBarIcon name="code" color={color} />,
          headerRight: () => (
            <Pressable
              onPress={() => navigation.navigate('Modal')}
              style={({ pressed }) => ({
                opacity: pressed ? 0.5 : 1,
              })}>
              <FontAwesome
                name="info-circle"
                size={25}
                color={'#0a9396'}
                style={{ marginRight: 15 }}
              />
            </Pressable>
          ),
        })}
      />
      <BottomTab.Screen
        name="TabTwo"
        component={TabTwoScreen}
        options={{
          title: 'Tab Two',
          tabBarIcon: ({ color }) => <TabBarIcon name="code" color={color} />,
        }}
      />
    </BottomTab.Navigator>
  );
  // return (
  //   <View style={styles.container}>
  //     <StatusBar />
  //     <View style={styles.column}>
  //       <Text style={styles.title}>Home Screen</Text>
  //       <Text style={styles.title}>Welcome: {auth.currentUser?.email}</Text>
  //       <Button title="Logout" onPress={handleSignOut}></Button>
  //     </View>
  //   </View>
  // );
}

const styles = StyleSheet.create({
  container: {
    flex: 1,
    backgroundColor: '#e93b81',
    paddingTop: 50,
    paddingHorizontal: 12
  },
  column: {
    flexDirection: 'column',
    justifyContent: 'space-between',
    alignItems: 'center',
    marginBottom: 24
  },
  title: {
    fontSize: 24,
    fontWeight: '600',
    color: '#fff'
  },
  text: {
    fontSize: 16,
    fontWeight: 'normal',
    color: '#fff'
  },
  headerStyle: {
    height: 80, // Specify the height of your custom header
  }
});

function TabBarIcon(props: {
  name: React.ComponentProps<typeof FontAwesome>['name'];
  color: string;
}) {
  return <FontAwesome size={30} style={{ marginBottom: -3 }} {...props} />;
}
