import * as WebBrowser from 'expo-web-browser';
import { StyleSheet, FlatList, ActivityIndicator, View } from 'react-native';

import { Button, ButtonGroup, withTheme, Text, Input, Icon, SearchBar, Image } from 'react-native-elements';
import { TextProps } from './Themed';


const CardItem = ({ ...props }) => (
    <View style={styles.CardContainer}>
        <Image style={styles.imageitem}
            source={{ uri: props.imagepath }}
            containerStyle={styles.imageitem}
            PlaceholderContent={<ActivityIndicator />}
        />
        <Text style={styles.contentContainer}>{props.title} 2</Text>
    </View>
);

export const PetCard = ({ item }: any) => (
    <CardItem title={item.title} />
);


const styles = StyleSheet.create({
    CardContainer: {
        flex: 1,
        flexDirection: 'row',
        flexWrap: 'wrap',
        alignItems: 'flex-start', // if you want to fill rows left to right
        borderRadius: 25,
        padding: 10,
        borderWidth: 1,
        borderColor: '#aaaaaa',
        marginVertical: 20
    },
    imageContainer: {
        width: '40%',
        aspectRatio: 1,
        backgroundColor: '#f9c2ff'
    },

    contentContainer: {
        width: '60%',
        backgroundColor: 'white'

    },
    title: {

    },
    imageitem: {}

});
