import { PetCard } from '../components/PetCard';
import { StyleSheet, FlatList, ActivityIndicator, View } from 'react-native';
import { ref, getDownloadURL } from "firebase/storage";
import { collection, query, where, onSnapshot, getDocs, orderBy, limit } from "firebase/firestore";
import React, { useEffect, useState } from 'react';
import { SettingStackProps, petPostDataProps } from '../types';
import { storage, db } from "../firebase";
import { LinearGradient } from 'expo-linear-gradient';
export function PetCardFlatList() {
    const [petPostDataArray, setpetPostDataArray] = useState<petPostDataProps[]>([]);
    const [isLoading, setIsLoading] = useState(true);

    const getData = async () => {


        const q = query(collection(db, "PetPost"), orderBy('createdate', "desc"), limit(1));
        const unsubscribe = onSnapshot(q, (querySnapshot) => {
            //setpetPostDataArray([])
            querySnapshot.forEach((doc) => {
                getDownloadURL(ref(storage, doc.data()['imagepath']))
                    .then((url) => {
                        console.log("petname:" + doc.data()['petname'] + " " + (doc.data()['createdate']).toDate())
                        const newjson = {
                            id: doc.id,
                            imagepath: url,
                            petname: doc.data()['petname'],
                            petgender: doc.data()['petgender'],
                            petstages: doc.data()['petstages'],
                            desc: doc.data()['desc']
                        }
                        //console.log(doc.data()['createdate'], newjson);
                        setpetPostDataArray(oldArray => [...oldArray, newjson]);
                    })
                    .catch((error) => {
                    });
            });
        });

    }

    useEffect(() => {
        getData()
        setIsLoading(false)
    }, []);

    return (
        <>
            {isLoading && <ActivityIndicator size="large" color='#298e96' />}
            <View style={styles.uppergadient}>
                <LinearGradient
                    colors={['rgba(255,255,255,1)', 'rgba(0,255,255,0)']}
                    style={{ height: 40 }}
                />
            </View>
            <FlatList
                showsVerticalScrollIndicator={false}
                showsHorizontalScrollIndicator={false}
                data={petPostDataArray}
                renderItem={PetCard}
                keyExtractor={item => item.id}
            />
            <View style={styles.lowergadient}>
                <LinearGradient
                    colors={['rgba(255,255,255,0)', 'rgba(255,255,255,1)']}
                    style={{ height: 40 }}
                />
            </View>

        </>

    );
}


const styles = StyleSheet.create({
    uppergadient: {
        flex: 1,
        backgroundColor: 'transparent',
        top: 0,
        width: '100%',
        height: 40,
        zIndex: 10
    },
    lowergadient: {
        flex: 1,
        backgroundColor: 'transparent',
        position: 'absolute',
        bottom: 0,
        width: '100%',
        height: 40
    }
});


function convertDate(firebaseObject: any) {
    if (!firebaseObject) return null;

    for (const [key, value] of Object.entries(firebaseObject)) {

        // covert items inside array
        if (value && Array.isArray(value))
            firebaseObject[key] = value.map(item => self.convertDate(item));

        // convert inner objects
        if (value && typeof value === 'object') {
            firebaseObject[key] = self.convertDate(value);
        }

        // convert simple properties
        if (value && value.hasOwnProperty('seconds'))
            firebaseObject[key] = (value as firestore.Timestamp).toDate();
    }
    return firebaseObject;
}
