import { StyleSheet, FlatList, ActivityIndicator, View } from 'react-native';
import { auth } from "../firebase";
import { RootTabScreenProps } from '../types';
import { Button, ButtonGroup, withTheme, Text, Input, Icon, SearchBar, Image } from 'react-native-elements';
import React, { useState } from 'react';

import { PetCatagory } from '../components/PetCatagory';
import { PetCardFlatList } from '../components/PetCardFlatList';
import { LinearGradient } from 'expo-linear-gradient';
import Constants from 'expo-constants'

export default function HomeScreen({ navigation }: RootTabScreenProps<'Home'>) {
  const [search, setSearch] = useState("");

  const updateSearch = (search: React.SetStateAction<string>) => {
    setSearch(search);
  };

  return (<>

    <View style={styles.container}>
      <View style={styles.headContainer}>
        <Text style={styles.header}>Good Morning</Text>
        <Text style={styles.headerDesc}>Are you ready to find a new friend</Text>
      </View>
      <View style={styles.SearchBarContainer}>
        {/* 
        // @ts-ignore */}
        <SearchBar
          placeholder="Search for pets"
          onChangeText={() => updateSearch}
          value={search}
          platform="default"
          round={true}
          lightTheme={true}
          containerStyle={styles.containerStyle}
          inputContainerStyle={styles.inputContainerStyle} />

      </View>
      <View style={styles.PetCatagoryContainer}>
        <FlatList
          showsVerticalScrollIndicator={false}
          showsHorizontalScrollIndicator={false}
          numColumns={5}
          data={petCatagoryData}
          renderItem={PetCatagory}
          keyExtractor={item => item.id}
        />
      </View>

      <Text style={styles.recent}>Recent</Text>
      <View style={styles.uppergadient}>
        <LinearGradient
          colors={['rgba(255,255,255,1)', 'rgba(0,255,255,0)']}
          style={{ height: 40 }}
        />
      </View>
      <PetCardFlatList />
    </View>
    <View style={styles.lowergadient}>
      <LinearGradient
        colors={['rgba(255,255,255,0)', 'rgba(255,255,255,1)']}
        style={{ height: 40 }}
      />
    </View>
  </>
  );
}


const petCatagoryData = [
  {
    id: '1',
    title: 'Cat',
  },
  {
    id: '2',
    title: 'Dog',
  },
  {
    id: '3',
    title: 'Bird',
  },
  {
    id: '4',
    title: 'Hamster',
  },
  {
    id: '5',
    title: 'Rabbit',
  },

];

const styles = StyleSheet.create({
  container: {
    flex: 1,
    padding: 20,
    paddingBottom: 0,
    backgroundColor: 'white',
    marginTop: Constants.statusBarHeight
  },
  PetCatagoryContainer: {
  },
  inputContainerStyle: {
    backgroundColor: 'white',
    borderColor: '#dddddd',
    borderTopWidth: 1,
    borderBottomWidth: 1,
    borderLeftWidth: 1,
    borderRightWidth: 1,
    height: 30
  },
  containerStyle: {
    backgroundColor: 'white',
    borderTopWidth: 0,
    borderBottomWidth: 0,
    padding: 0,
    height: 30

  },
  headContainer: {
    overflow: 'hidden',
    justifyContent: 'space-between',
    paddingTop: 15,
    paddingBottom: 15
  },
  SearchBarContainer: {
    paddingBottom: 15
  },
  header: {
    fontSize: 30,
    fontWeight: 'bold',
    color: '#298e96'
  },
  headerDesc: {
    fontSize: 15,
    color: '#aaaaaa'
  },
  recent: {
    fontWeight: 'bold',
    fontSize: 20
  },
  uppergadient: {
    flex: 1,
    backgroundColor: 'transparent',
    top: 0,
    width: '100%',
    height: 40,
    zIndex: 10
  },
  lowergadient: {
    flex: 1,
    backgroundColor: 'transparent',
    position: 'absolute',
    bottom: 0,
    width: '100%',
    height: 40
  }
});
