import { LinearGradient } from 'expo-linear-gradient';
import { collection, doc, getDocs, onSnapshot, orderBy, query } from "firebase/firestore";
import React, { useEffect, useState } from 'react';
import { FlatList, ScrollView, StyleSheet, View } from 'react-native';
import { Avatar, Text } from 'react-native-elements';
import { SocialMediaFlatList } from '../components/SocialMediaFlatList';
import { db } from "../firebase";
import { socialMediaDataProps } from '../types';




export default function PetSocialMediaScreen({ route, navigation }: { route: any, navigation: any }) {
  const [ownerName, setOwnerName] = useState<string | undefined>('');
  const [ownerImagepath, setOwnerImage] = useState("");
  const [petSocialMediaPost, setPetSocialMediaPost] = useState([]);

  const [petPostDataItem, setPetPostDataItem] = useState(route.params.petPostDataItem.item)
  const [socialMediaDataArray, setpetPostDataArray] = useState<socialMediaDataProps[]>([]);

  const getOwnerDetail = async () => {
    if (!petPostDataItem.postcreator) return false;

    const unsub = onSnapshot(doc(db, "Users", petPostDataItem.postcreator), (doc) => {
      if (doc == undefined) return false;
      if (!doc.data()) return false;
      setOwnerName(doc.data()?.username)
      setOwnerImage(doc.data()?.userIconUri)
    });
  }

  const getData = async () => {

    var postRef = doc(db, "PetPost", petPostDataItem.id);
    var socialMediaPostRef = collection(postRef, "socialMediaPost");
    const q = query(socialMediaPostRef, orderBy('createDate', 'desc'));
    setpetPostDataArray([])
    const querySnapshot = await getDocs(q);
    querySnapshot.forEach((docSnap) => {
      const newjson = {
        id: docSnap.id,
        imagepath: docSnap.data()['imagepath'],
        caption: docSnap.data()['caption'],
        createDate: docSnap.data()['createDate'],
      }

      setpetPostDataArray(oldArray => [...oldArray, newjson]);
    });
  }


  useEffect(() => {
    getData()
    getOwnerDetail();
  }, []);

  return (
    <>
      <View style={styles.container}>
        <View style={styles.iconContainer}>
          {(petPostDataItem.imagepath != "" && petPostDataItem.imagepath != undefined) ?

            <Avatar
              size={100}
              rounded
              source={{ uri: petPostDataItem.imagepath }}
            />
            : <Avatar
              size={100}
              rounded
              title={petPostDataItem.petname ? petPostDataItem.petname[0] : ""}
              containerStyle={{ backgroundColor: '#aaaaaa' }}
            />
          }

          <View style={styles.iconDescriptionContainer}>
            <Text style={styles.petname}>{petPostDataItem.petname}</Text>
            <Text style={styles.petgender}>{petPostDataItem.petgender}  -  {petPostDataItem.petstages} </Text>
          </View>


        </View>

        <View style={styles.contentContainer}>
          <Text style={{ fontSize: 14, marginTop: 12, fontWeight: 'bold' }}>Recent Post</Text>
          <View style={styles.uppergadient}>
            <LinearGradient
              colors={['white', 'rgba(255,255,255,0)']}
              style={{ height: 40 }}
            />
          </View>
          <ScrollView>
            <SocialMediaFlatList petPostDataItem={petPostDataItem} />
            <FlatList
              showsVerticalScrollIndicator={true}
              showsHorizontalScrollIndicator={false}
              data={socialMediaDataArray}
              renderItem={(item) => <></>}
              keyExtractor={item => item.id}
            />

          </ScrollView>
          <View style={styles.lowergadient}>
            <LinearGradient
              colors={['rgba(255,255,255,0)', 'rgba(255,255,255,1)']}
              style={{ height: 40 }}
            />
          </View>
        </View>

      </View >
    </>
  );
}

const styles = StyleSheet.create({
  container: {
    flex: 1,
    backgroundColor: 'white',
    height: "100%",
    flexDirection: 'column'
  },
  iconContainer: {
    padding: 20,
    alignSelf: 'flex-start',
    zIndex: 1,
    flexDirection: 'row'
  },
  iconDescriptionContainer: {
    marginLeft: 20,
    alignSelf: 'flex-start'
  },

  contentContainer: {
    width: '100%',
    height: '100%',
    padding: 30,

    flex: 1,
    borderRadius: 25,
    backgroundColor: 'white',
    top: -30
  },
  iconContainer2: {
    marginTop: 40,
    padding: 20,
    alignSelf: 'flex-end',
    zIndex: 1,
    position: 'absolute'
  },
  imageitem: {
    zIndex: 0,
    aspectRatio: 1.1,
    width: '100%',
  },

  petname: {
    fontWeight: 'bold',
    fontSize: 30
  },
  petgender: {
    fontSize: 12, marginBottom: 12, fontWeight: 'bold', color: '#888888'
  },
  paragraph: {
    fontSize: 12, color: '#999999'
  },
  required: {
    fontSize: 12, color: 'red'
  },
  fulfilled: {
    fontSize: 12, color: 'green'
  },
  paragraphTitle: {
    fontSize: 12, color: '#999999', fontWeight: 'bold'
  },
  contactbutton: {
    backgroundColor: '#0b9298',
    borderRadius: 5,
    paddingHorizontal: 15,
    marginRight: 30
  },
  contactbuttonContainer: {
    marginHorizontal: 30
  },
  footerContainer: {
    position: 'absolute',
    flexDirection: 'row',
    justifyContent: 'space-between',
    alignItems: 'center',
    bottom: 0,
    width: '100%',
    height: '10%',
  },
  ownerContainer: {
    marginLeft: 30,
    flexDirection: 'row',
    justifyContent: 'flex-start',
  },
  ownerDetailContainer: {
    marginLeft: 10,
    flexDirection: 'column',
    justifyContent: 'center',
    alignItems: 'flex-start'
  },


});
