import React, { useEffect, useState, useContext } from 'react';
import { StyleSheet, View, Image, TouchableOpacity, ScrollView, Platform } from 'react-native';
import { Button, ButtonGroup, withTheme, Text, Input, Divider } from 'react-native-elements';
import * as ImagePicker from 'expo-image-picker';
import * as FileSystem from 'expo-file-system';

import { storage, db, auth } from "../firebase";
import { ref as storage_ref, getDownloadURL, uploadBytes } from "firebase/storage";
import { collection, query, where, onSnapshot, doc, updateDoc } from "firebase/firestore";

import { SettingStackProps } from '../types';
import Dropdown from '../components/dropdown'

export default function UserSettingScreen({ navigation }: SettingStackProps<'Setting'>) {
  const [imagepath, setImage] = useState("");
  const [downloadURL, setDownloadURL] = useState("");
  const [UserGender, setGenderValue] = useState<number | string>("Male");
  const [UserStage, setStageValue] = useState<number | string>("Kid");
  const [UserName, setUserName] = useState('');
  const [UserAge, setUserAge] = useState<number | string>('');
  const [UserDescription, setUserDescription] = useState('');
  const [uploading, setUploading] = useState(false);
  const [submitted, setSubmitted] = useState(false);
  const [selectedButtonIndex, setSelectedButtonIndex] = useState(2);
  const [submitButtomText, setSubmitButtomText] = useState('Submit');


  const pickImageByLibrary = async () => {
    let result = await ImagePicker.launchImageLibraryAsync({
      mediaTypes: ImagePicker.MediaTypeOptions.All,
      allowsEditing: true,
      aspect: [4, 3],
      quality: 1,
    });
    if (!result.cancelled) {
      setImage(result.uri);
    }

  };

  const pickImageByCamera = async () => {
    let result = await ImagePicker.launchCameraAsync({
      mediaTypes: ImagePicker.MediaTypeOptions.All,
      allowsEditing: true,
      aspect: [4, 3],
      quality: 1,
    });
    if (!result.cancelled) {
      setImage(result.uri);
    }
    console.log(Platform.OS)
  };

  const uploadPost = async () => {
    setUploading(true)

    const userId = auth.currentUser?.uid;
    console.log('user uploading image ', userId);

    var snapshotDownloadURL = null
    if (imagepath) {
      const imagename = Date.now() + '-' + getRandomString(5) + '-' + userId
      const storageRef = storage_ref(storage, 'userIcon/' + imagename + '.png');

      const blob: Blob = await new Promise((resolve, reject) => {
        const xhr = new XMLHttpRequest();
        xhr.onload = function () {
          resolve(xhr.response);
        };
        xhr.onerror = function () {
          reject(new TypeError("Network request failed"));
        };
        xhr.responseType = "blob";
        xhr.open("GET", imagepath, true);
        xhr.send(null);
      });
      const snapshot = await uploadBytes(storageRef, blob);
      snapshotDownloadURL = await getDownloadURL(snapshot.ref);
      setDownloadURL(snapshotDownloadURL);
      console.log('File available at', snapshotDownloadURL);
    } else {
      console.log('user no image ', userId);
    }
    try {
      if (userId) {
        const UserRef = doc(db, 'Users', userId);
        await updateDoc(UserRef, {
          imagepath: snapshotDownloadURL,
          username: UserName,
          usergender: UserGender,
          desc: UserDescription,
          lastupdateDate: new Date(),
          userAge: UserAge
        });
        console.log("Document written with ID: ", UserRef.id);
      } else {
        console.error("userId not found:  ", auth.currentUser);
      }
      // const docRef = await addDoc(collection(db, "Users"), {

      // });



    } catch (e) {
      console.error("Error adding document:  ", e);
    }

    setSubmitted(true)
    setUploading(false)
    setSubmitButtomText("Submitted")
  }

  return (
    <>
      <View style={styles.container}>
        <ScrollView showsVerticalScrollIndicator={false}
          showsHorizontalScrollIndicator={false}
          style={{ height: "100%", width: "100%" }}>
          <View style={styles.imagePickerContainer}>
            {imagepath != "" &&
              <TouchableOpacity onPress={pickImageByLibrary}>
                <Image style={styles.imageitem}
                  source={{ uri: imagepath }}
                />

                <Avatar
                  size={50}
                  rounded
                  source={{ uri: 'https://cdn.pixabay.com/photo/2019/11/03/20/11/portrait-4599553__340.jpg' }}
                />
              </TouchableOpacity>
            }
            <View style={styles.paddingView} />
            <ButtonGroup
              buttons={['Select an image', 'Open camera']}
              selectedIndex={selectedButtonIndex}
              onPress={(value) => {
                setSelectedButtonIndex(value);
                if (value == 0) {
                  pickImageByLibrary()
                }
                if (value == 1) {
                  pickImageByCamera()
                }
              }}
              containerStyle={{ marginBottom: 20 }}
            />

          </View>
          <Button title={submitButtomText} onPress={uploadPost}
            loading={uploading}
            disabled={submitted}
            buttonStyle={{
              backgroundColor: 'rgba(111, 202, 186, 1)',
              borderRadius: 5,
              margin: 10, marginTop: 30
            }}

            titleStyle={{ fontSize: 12 }} />
          <Input style={styles.input} placeholder="Charlie" label='User Name:' onChangeText={text => setUserName(text)} containerStyle={{ marginBottom: 20 }} />
          <Input style={styles.input} placeholder="0-100" label='User Age:' onChangeText={text => setUserAge(text)} containerStyle={{ marginBottom: 20 }} />
          <Input style={styles.input} placeholder="Descript your User here" label='User Description:' onChangeText={text => setUserDescription(text)} containerStyle={{ marginBottom: 20 }} />
          <View style={styles.pickerContainer}>
            <Text style={styles.pickerLabel} >User Gender:</Text>
            <Dropdown data={gender_drop_down_data} />
          </View>

        </ScrollView>
      </View >
    </>
    //imagepath={imagepath} Username={UserName} Usergender={UserGender} Userstages={UserStage} desc={UserDescription} 
  );
}

const styles = StyleSheet.create({
  container: {
    flex: 1,
    alignItems: 'flex-start',
    justifyContent: 'flex-start',
    padding: 20,
    backgroundColor: 'white',
    height: "100%"
  },
  title: {
    fontSize: 20,
    fontWeight: 'bold',
  },
  separator: {
    marginVertical: 30,
    height: 1,
    width: '80%',
  },
  input: {
    fontSize: 12
  },
  pickerContainer: {
    paddingHorizontal: 10,
    marginBottom: 30

  },
  pickerLabel: {
    fontSize: 16,
    fontWeight: 'bold',
    color: 'rgb(134, 147, 158)'
  },
  CardContainer: {
    width: '100%',
    flex: 1,
    flexDirection: 'row',
    flexWrap: 'wrap',
    alignItems: 'flex-start', // if you want to fill rows left to right
    borderRadius: 25,
    padding: 10,
    borderWidth: 1,
    borderColor: '#dddddd',
    marginVertical: 15,
  },
  imageitem: {
    aspectRatio: 0.95,
    width: 200,
    borderRadius: 25,
    fontSize: 12,
    alignself: 'center'

  },
  contentContainer: {
    width: '50%',
    height: '100%',
    paddingLeft: 10,
    flex: 1,
  },
  Username: {
    fontWeight: 'bold',
    fontSize: 20
  },
  Usergender: {
    fontSize: 10,
    marginBottom: 12,
    fontWeight: 'bold',
    color: '#888888'
  },
  desc: {
    fontSize: 10,
    color: '#999999'
  },
  askbutton: {
    backgroundColor: '#0b9298',
    borderRadius: 5,
    paddingHorizontal: 15
  },
  askbuttonContainer: {
    position: 'absolute',
    left: 10,
    bottom: 0,
  },
  imagePickerContainer: {
    flexDirection: 'column',
    paddingHorizontal: 10
  },
  paddingView: {
    paddingTop: 10
  }
});

const gender_drop_down_data = [
  { label: 'Male', value: 'male' },
  { label: 'Female', value: 'female' }
];

const User_stage_drop_down_data = [
  { label: 'Bady', value: 'bady' },
  { label: 'Kid', value: 'kid' },
  { label: 'Adult', value: 'adult' }
];

function getRandomString(length: number) {
  var randomChars = 'ABCDEFGHIJKLMNOPQRSTUVWXYZabcdefghijklmnopqrstuvwxyz0123456789';
  var result = '';
  for (var i = 0; i < length; i++) {
    result += randomChars.charAt(Math.floor(Math.random() * randomChars.length));
  }
  return result;
}
