import React, { useEffect, useState } from 'react';
import * as WebBrowser from 'expo-web-browser';
import { ReactChild, ReactFragment, ReactPortal, useContext } from 'react';
import { StyleSheet, FlatList, ActivityIndicator, View, Image, Pressable } from 'react-native';
import { petPostDataProps } from '../types';
import { ButtonGroup, withTheme, Text, Input, Icon, SearchBar, Button, Badge } from 'react-native-elements';
import { NativeStackNavigationProp } from '@react-navigation/native-stack';

const assetsObject = require('../assets/asset.tsx')

export const PetRequestStatusCard = (petPostDataItem: any, navigation: any) => (
    <View style={styles.CardContainer}>
        <Badge value={petPostDataItem.item.requestUsers?.length} status="success" textStyle={{ fontSize: 12 }}
            containerStyle={{ position: 'absolute', top: -5, right: 40 }}
        />
        {petPostDataItem.item.imagepath != "" && <Image style={styles.imageitem}
            source={{ uri: petPostDataItem.item.imagepath }}
        />}
        <View style={styles.contentContainer}>

            <Text style={styles.petname}>{petPostDataItem.item.petname}</Text>
            <Text style={styles.petgender}>{petPostDataItem.item.petgender}  -  {petPostDataItem.item.petstages} </Text>
            <Text >{petPostDataItem.requestUsers} </Text>

            <Button buttonStyle={styles.askbutton}
                containerStyle={styles.askbuttonContainer}
                titleStyle={{ fontWeight: 'bold', fontSize: 11 }}
                title="View Requests"
                onPress={() => {
                    return navigation.navigate('ViewMyPost');
                }}
            >
            </Button>
        </View>
    </View>
);

const styles = StyleSheet.create({
    CardContainer: {
        flex: 1,
        flexDirection: 'row',
        flexWrap: 'wrap',
        alignItems: 'flex-start', // if you want to fill rows left to right
        borderRadius: 25,
        padding: 10,
        borderWidth: 1,
        borderColor: '#dddddd',
        marginVertical: 15,
    },
    contentContainer: {
        width: '50%',
        height: '100%',
        paddingLeft: 10,
        flex: 1,
    },
    petname: {
        fontWeight: 'bold',
        fontSize: 20
    },
    petgender: { fontSize: 10, marginBottom: 12, fontWeight: 'bold', color: '#888888' },
    desc: { fontSize: 10, color: '#999999' },
    imageitem: {
        aspectRatio: 0.95,
        width: '50%',
        borderRadius: 25,
    },
    askbutton: {
        backgroundColor: '#0b9298',
        borderRadius: 5,
        paddingHorizontal: 15
    },
    askbuttonContainer: {
        position: 'absolute',
        left: 10,
        bottom: 0,
    }

});
