import React, { useEffect, useState, useContext } from 'react';
import { StyleSheet, View, Image, Alert } from 'react-native';
import { Button, ButtonGroup, withTheme, Text, Input, Divider, Avatar } from 'react-native-elements';
import * as ImagePicker from 'expo-image-picker';
import * as FileSystem from 'expo-file-system';
import { FontAwesome } from '@expo/vector-icons';
import { storage, db, auth } from "../firebase";
import { ref as storage_ref, getDownloadURL, uploadBytes } from "firebase/storage";
import { collection, query, where, onSnapshot, getDocs, addDoc, doc, setDoc } from "firebase/firestore";

import { SettingStackProps, petPostDataProps } from '../types';
import Dropdown from '../components/dropdown'

export default function PostDetailScreen({ route, navigation }: { route: any, navigation: any }) {
  const { petItem } = route.params;
  const [ownerName, setOwnerName] = useState<string | undefined>('');
  const [ownerImagepath, setOwnerImage] = useState("");

  const getOwnerDetail = async () => {
    if (!petItem.postcreator) return false;

    const unsub = onSnapshot(doc(db, "Users", petItem.postcreator), (doc) => {
      if (doc == undefined) return false;
      if (!doc.data()) return false;
      setOwnerName(doc.data()?.username)
      setOwnerImage(doc.data()?.imagepath)
    });
  }

  const sendContactRequest = async () => {
    var userId = auth.currentUser?.uid;

    if (petItem.postcreator == userId) {
      Alert.alert(
        "Request Failed", "You cannot adopt your own pet",
        [
          { text: "OK", onPress: () => console.log("OK Pressed") }
        ]
      );
      return false;
    }

    try {
      if (!userId) {
        console.error("userId not found:  ", auth.currentUser);
        return false;
      }

      var postRef = doc(db, "PetPost", petItem.id);
      var requestRef = doc(postRef, "contactRequest", userId);

      const unsub = await setDoc(requestRef, {
        status: 'New request',
        requestTime: new Date(),
      }, { merge: true });

      console.log("Document written with ID: ", petItem.id);

    } catch (e) {
      console.error("Error adding document:  ", e);
    }

  }


  useEffect(() => {
    getOwnerDetail()
  }, []);

  return (
    <>
      <View style={styles.container}>
        <View style={styles.iconContainer}>
          <FontAwesome
            name="arrow-left"
            size={25}
            color="white"
            onPress={() => { navigation.navigate('Home') }}
          />
        </View>
        <View style={styles.iconContainer2}>
          <FontAwesome
            name="heart-o"
            size={25}
            color="white"
          />
        </View>
        {petItem.imagepath != "" && <Image style={styles.imageitem}
          source={{ uri: petItem.imagepath }}
        />}
        <View style={styles.contentContainer}>
          <Text style={styles.petname}>{petItem.petname}</Text>
          <Text style={styles.petgender}>{petItem.petgender}  -  {petItem.petstages} </Text>
          <Text style={{ fontSize: 16, marginTop: 12, fontWeight: 'bold', }}>Pet Story</Text>
          <Text style={styles.desc}>{petItem.desc} </Text>

        </View>

        <View style={styles.footerContainer}>
          <View style={styles.ownerContainer}>
            <Avatar
              size={50}
              rounded
              source={{ uri: ownerImagepath }}
            />
            <View style={styles.ownerDetailContainer}>
              <Text style={{ fontWeight: 'bold', fontSize: 12 }}>{ownerName}</Text>
              <Text style={{ fontSize: 10, color: '#aaaaaa' }}>owner</Text>
            </View>
          </View>
          <Button buttonStyle={styles.contactbutton}
            containerStyle={styles.contactbuttonContainer}
            titleStyle={{ fontWeight: 'bold', fontSize: 11 }}
            title="Send Adopt Request"
            onPress={() => {
              sendContactRequest()
            }}
          >
          </Button>
        </View>
      </View >
    </>
  );
}

const styles = StyleSheet.create({
  container: {
    flex: 1,
    alignItems: 'flex-start',
    justifyContent: 'flex-start',
    backgroundColor: 'white',
    height: "100%"
  },
  iconContainer: {
    marginTop: 40,
    padding: 20,
    alignSelf: 'flex-start',
    zIndex: 1,
    position: 'absolute'
  },
  iconContainer2: {
    marginTop: 40,
    padding: 20,
    alignSelf: 'flex-end',
    zIndex: 1,
    position: 'absolute'
  },
  imageitem: {
    zIndex: 0,
    aspectRatio: 1.1,
    width: '100%',
  },
  contentContainer: {
    width: '100%',
    height: '100%',
    padding: 30,
    flex: 1,
    borderRadius: 25,
    backgroundColor: 'white',
    top: -30
  },
  petname: {
    fontWeight: 'bold',
    fontSize: 30
  },
  petgender: {
    fontSize: 10, marginBottom: 12, fontWeight: 'bold', color: '#888888'
  },
  desc: {
    fontSize: 10, color: '#999999'
  },
  contactbutton: {
    backgroundColor: '#0b9298',
    borderRadius: 5,
    paddingHorizontal: 15,
    marginRight: 30
  },
  contactbuttonContainer: {
    marginHorizontal: 30
  },
  footerContainer: {
    position: 'absolute',
    flexDirection: 'row',
    justifyContent: 'space-between',
    alignItems: 'center',
    bottom: 0,
    width: '100%',
    height: '10%',
  },
  ownerContainer: {
    marginLeft: 30,
    flexDirection: 'row',
    justifyContent: 'flex-start',
  },
  ownerDetailContainer: {
    marginLeft: 10,
    flexDirection: 'column',
    justifyContent: 'center',
    alignItems: 'flex-start'
  },


});
