import React from 'react';
import { StyleSheet, View, Pressable } from 'react-native';
import { auth } from "../firebase";
import { FontAwesome } from '@expo/vector-icons';
import EditScreenInfo from '../components/EditScreenInfo';
import { Button, ButtonGroup, withTheme, Text, Input } from 'react-native-elements';
import { HomeStackParamList, HomeStackProps } from '../types';
import { createNativeStackNavigator } from '@react-navigation/native-stack';
import HomeScreen from '../screens/HomeScreen';
import PostDetailScreen from '../screens/PostDetailScreen';
const Stack = createNativeStackNavigator<HomeStackParamList>();

export default function HomeScreenNav({ navigation }: HomeStackProps<'Home'>) {
  const NavContext = React.createContext(navigation)

  return (
    <Stack.Navigator>
      <NavContext.Provider value={{ theme, setTheme }}>
        <Stack.Screen options={{ headerShown: false }} name="Home" component={HomeScreen}
        ></Stack.Screen>
        <Stack.Screen name="PostDetail" component={PostDetailScreen}
          options={{
            title: 'PostDetatil',
            headerLeft: () => (
              <Pressable
                onPress={() => navigation.navigate('Home')}
                style={({ pressed }) => ({
                  opacity: pressed ? 0.5 : 1,
                  marginLeft: 20
                })}>
                <FontAwesome
                  name="arrow-left"
                  size={25}
                  color="black"
                  style={{ marginRight: 15 }}
                />
              </Pressable>
            ),
          }}
        ></Stack.Screen>
      </NavContext.Provider>

    </Stack.Navigator>
  );
}

const styles = StyleSheet.create({
  container: {
    flex: 1,
    alignItems: 'center',
    justifyContent: 'center',
  },
  title: {
    fontSize: 20,
    fontWeight: 'bold',
  },
  separator: {
    marginVertical: 30,
    height: 1,
    width: '80%',
  },
});
