import React from 'react';
import { Image, StyleSheet, View, TouchableHighlight } from 'react-native';
import { Card, Text } from 'react-native-elements';


export const PetSocialMediaCard = (petPostDataItem: any, navigation: any) => (
    <View style={styles.CardContainer}>
        {
            petPostDataItem.item.imagepath != "" &&
            <View>
                <TouchableHighlight
                    onPress={this._onPressButton}
                    onLongPress={this._onLongPressButton} underlayColor="white">

                    <Image style={styles.imageitem}
                        source={{ uri: petPostDataItem.item.imagepath }}
                    />
                </TouchableHighlight >


                <Dialog isVisible={uploadingImage}>
                    <Text>Uploading Icon Image, Please Wait</Text>
                    <Dialog.Loading />
                </Dialog>
            </View>

        }
    </View >
);

const styles = StyleSheet.create({
    CardContainer: {
        width: '33%',
        borderColor: '#fefefe',
        borderWidth: 1
    },
    imageitem: {
        aspectRatio: 1,
    },
});

